   const messages = useMessages();
   const [isConnected, setIsConnected] = useState(false);//client.isConnected());


























 function sendMessage(e) {
    e.preventDefault();
    client.getClient().send(message);
    setMessage('');
  }








return(

<div className={classes.chatWindow}>

<ChatUserInfoTopBar clickedUserId={props.clickedUserId} />

<ChatScreen messages={state.messages}  commentObj={commentObj}/>

<div className={classes.chatInput}>





     <form className={classes.addCommentForm} onSubmit={sendMessage}>


                  <div className={classes.chatInput} >

                      <input className={classes.inputTextBox} value={message} onChange={e => setMessage(e.target.value)} />



                  </div>


                 <button type="submit"  className= {classes.submit_button} ><b><MdSend className={classes.sendButtonIcon}/> </b> </button>

     </form>


</div>




</div>

);

}

export default ChatWindow;
