import React,{useState,useEffect} from 'react';
import classes from './NoticeBox.module.css';
import {BsCheckCircle} from 'react-icons/bs';


const NoticeBox =(props)=>{



const [bkgColor, setBkgColor] = useState('white');




useEffect(()=>{

	props.read && setBkgColor(bkgColor=>'#E8E8E8');

},[props.read]);	


    const creationTime=({datetime})=>{


     let date= datetime.split("T").at(0);
     let time_ = datetime.split("T").at(1);


     let timehour=datetime.split("T").at(1).split(":").at(0);
     let timemin=datetime.split("T").at(1).split(":").at(1);
     let timesec=datetime.split("T").at(1).split(":").at(2);

     let totalTime=timehour+":"+timemin+":"+timesec;

     let timeFormat =  date+" "+totalTime+" "+"UTC";

     //console.log("datetime: ", timeFormat);

     let DatetimeLocalFull = new Date(timeFormat);


     let DatetimeLocalFullStr= String(DatetimeLocalFull);

     let dayStr=DatetimeLocalFullStr.split(" ").at(0);
     let dateStr=DatetimeLocalFullStr.split(" ").at(2);
     let month= DatetimeLocalFullStr.split(" ").at(1);

     let fullTimeLocal = DatetimeLocalFullStr.split(" ").at(4);

     let fullTimeLocalStr = String(fullTimeLocal);

     let localTimeHour = fullTimeLocalStr.split(":").at(0);
     let localTimeMin = fullTimeLocalStr.split(":").at(1);



     let ampm;
     if(localTimeHour < 12){
      ampm="am";
     }
     if(localTimeHour > 12){
       localTimeHour=localTimeHour-12;
       ampm="pm"
     }

     let time=String(localTimeHour)+":"+String(localTimeMin)+String(ampm)+", "+String(dayStr)+", "+String(dateStr)+" "+String(month);

     return time;

   }


   let datetime= props.Notice.creationTime;


















return (

<div className={classes.noticeBox} style={{backgroundColor:bkgColor}}>


<div className={classes.topSection}> 
	<i className={classes.noticeID}>Notice ID: EDR-{props.Notice.id}</i>  
	<i className={classes.creationTime}> Created at: {commentTime({datetime})}</i>  

</div>


<div className={classes.midSection}> <div> <h2>  {props.Notice.noticeTitle}   </h2> <p>{props.Notice.noticeText}</p></div></div>

<div className={classes.buttomSection}> 

<button className={classes.noticeBoxButtonView}> <i>detail view</i></button>


{props.read &&  <BsCheckCircle className={classes.noticeReadIcon}/>}	


	{!props.read && <button className={classes.noticeBoxButtonRead} onClick={props.markAsRead}> 

 	<i>mark as read</i>

        </button>}

<button className={classes.noticeBoxButtonHide}> <i>hide</i></button>


</div>	




</div>

);

}


export default NoticeBox;
