import React from 'react';
import classes from './CoursesGridContainer.module.css';
import CourseViewDashboard from './CourseViewDashboard';
//import {getteachercourses,getuser, getcoursesbyCourseId, getcoursesbyglobalCodeArray} from '../../../../../CommonApps/AllAPICalls';


const CoursesGridContainer =(props)=>{




/*

const [data, setData] = useState({
"dashboardcourses": []
});
const [courseData,getCourseData] = useState([]);

useEffect(()=>{
     getuser({setData});
  },[])


useEffect(()=>{
 let courseIds = data.dashboardcourses;
 console.log('courseIds:', courseIds);	
