import React,{useState,useEffect} from "react";
import classes from "./LeftUserBoxContainer.module.css"
import LeftNewUserBox from "./LeftNewUserBox";
import classimg from '../../../../../images/groupImage.png';
import user1Image from '../../../../../images/girl1.jpeg'
import {getallusers,getuser,createchatgroup} from '../../../../../CommonApps/AllAPICalls';

import basewebURL from "../../../../../basewebURL";


const LeftUserBoxContainer = (props) =>{



let groupname="Classical Mechanics-I";
let user1="Reena Samant";

const [usersData, setUsersData ] = useState(props.usersData);
const [data, setData]=useState(props.data );








useEffect(()=>{

setUsersData(usersData=>props.usersData);
setData(data=>props.data);

},[props.usersData,props.data]);



const createGroupHandler = (userId) =>{

console.log("clicked user: ", userId);
console.log("loggedIn user: ", data.id);

const groupMembers=[userId,data.id];

const maxId = Math.max.apply(null, groupMembers);
const minId = Math.min.apply(null, groupMembers);	

console.log('max: ', maxId);
console.log('min: ', minId);	
const groupname='a'+maxId+'a'+minId+'a';

createchatgroup({groupname,groupMembers});

}







return(

<div className={classes.leftUserBoxContainer}>



      {/* in future change this logic. This must interfere performance*/}

      {usersData.filter(
           function(user) {
         if (user.id === data.id) {
              return false; 
              }
                  return true;
             })


            .map((user,index)=>{



             return    <LeftNewUserBox key={index} userImage={user.profile_image} userName={user.firstname +" "+user.lastname}  onPress={()=>createGroupHandler(user.id)}   />



           }

        )}







</div>

);

}

export default LeftUserBoxContainer;
