import React,{useRef,useEffect} from "react";
import classes from "./ChatScreen.module.css";


const ChatScreen=(props)=>{


const messages=props.messages;

    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
       messagesEndRef.current?.scrollIntoView({ behavior: "smooth" })
    }



    useEffect(() => {
      scrollToBottom();
    }, [messages]);


   const commentTime=()=>{


   }


   let datetime="2022-07-24T04:37:11.964957Z";
   let date= datetime.split("T").at(0);
   let timehour=datetime.split("T").at(1).split(":").at(0);
   let timemin=datetime.split("T").at(1).split(":").at(0);	
   console.log("date: ", date);	
   console.log("time: ", timehour,": " ,timemin);













return(

<div className={classes.chatScreen}>


     <div className={classes.dummyDiv1}>  Bibhuprasad   </div>


      {props.commentObj.map((comment,index)=>{

               return <div  key={index} className={classes.comment} >  

		      {comment.commenter === props.currentUser &&
		         <i className={classes.commentText}>
			     <span> {comment.commenttext}</span>
			     <span className={classes.chatTime}> <span className={classes.timeText}>{comment.commenttime}</span></span>
			</i> 
                      }

                      {comment.commenter !== props.currentUser &&

                       <i className={classes.commentText} style={{backgroundColor: 'white', float: 'left',marginLeft: '100px',boxShadow: 'rgba(99, 99, 99, 0.2) 0px 2px 8px 0px'}}>{comment.commenttext}</i>

                      }

		      </div>




           }

        )}






         { messages.map((message,index)=>{

               let allMsg = message.split(" ");

	       let userId = allMsg[allMsg.length-2];	

	       //let displayMsg1 = allMsg.pop();
	       //let displayMsg2 = allMsg.pop(); 

               return <div key={index} className={classes.comment}>   


                    {Number(userId) === props.currentUser &&

                          <i className={classes.commentText}>  {allMsg.join(" ")} </i>

                     }


                     {Number(userId) !== props.currentUser &&

                          <i className={classes.commentText} style={{backgroundColor: 'white', float: 'left',marginLeft: '100px',boxShadow: 'rgba(99, 99, 99, 0.2) 0px 2px 8px 0px'}}>  {allMsg.join(" ")} </i>

                     }






		      </div>

           }

        ) 
      } 



     <div className={classes.dummyDiv} ref={messagesEndRef}>   </div>	









</div>	
);


}


export default ChatScreen;
