import React,{useState} from 'react';
import classes from './VideoPlayer.module.css';
import './VideoPlayer.css';

import VideoShot from './VideoShot.png';

import {FaMicrophone, FaMicrophoneSlash} from 'react-icons/fa';
import {BsFillCameraVideoFill, BsFillCameraVideoOffFill,BsFillTelephoneXFill, BsFillChatLeftTextFill} from 'react-icons/bs';

import {FiShare} from 'react-icons/fi';

import {GiCctvCamera} from 'react-icons/gi';


const VideoPlayer=()=>{


const [playing, setPlaying] = useState(false);

     const HEIGHT = 500;
     const WIDTH = 500;



const startVideo = () => {
		setPlaying(true);
		navigator.getUserMedia(
			{
				video: true,
			},
			(stream) => {
				let video = document.getElementsByClassName('app__videoFeed')[0];
				if (video) {
					video.srcObject = stream;
				}
			},
			(err) => console.error(err)
		);
	};



const stopVideo = () => {
		setPlaying(false);
		let video = document.getElementsByClassName('app__videoFeed')[0];
		video.srcObject.getTracks()[0].stop();
	};















 return (


   <div className={classes.videoPlayer}>


        <div className={classes.videoDiv}>
	   {/*
            <img className={classes.videoImage} src={VideoShot} alt='edr Logo' />
            */}
                              <video
                                        height={HEIGHT}
                                        width={WIDTH}
                                        muted
                                        autoPlay
                                        className="app__videoFeed"
                                        >    
	                       </video>



        </div>








        <div className={classes.toolsDiv}>

            <div className={classes.timeInfo}> 5:30pm  </div>

            <div className={classes.buttonIcons}>
                   <FaMicrophone className={classes.Icon}/>

                   <BsFillCameraVideoFill className={classes.Icon}/>

                    {playing ? (
                                        <button onClick={stopVideo} className={classes.Icon} > <BsFillCameraVideoFill/> </button>
                                ) : (
                                        <button onClick={startVideo} className={classes.Icon} > <BsFillCameraVideoOffFill/> </button>
                     )}




                   <GiCctvCamera className={classes.Icon}/>

                   <FiShare className={classes.Icon}/>

                   <BsFillTelephoneXFill className={classes.Icon3} />

            </div>

            <div className={classes.buttonIcons}>

            <BsFillChatLeftTextFill className={classes.IconChat}/>

           </div>


        </div>


   </div>









 );






}


export default VideoPlayer;
