import React,{useState,useEffect} from "react";
import classes from "./ChatWindow.module.css"
import ChatUserInfoTopBar from "./ChatUserInfoTopBar";
import ChatScreen from "./ChatScreen";
import ChatInput from "./ChatInput";
import {w3cwebsocket as W3CWebSocket } from 'websocket';




const ChatWindow = () =>{


let roomname='class'

const [message,setMessage]=useState([]);


const client = new W3CWebSocket('ws://127.0.0.1:8000/ws/chat/class/');


   const initialFormData = Object.freeze({
        comment: "",
        });

   const [formData, updateFormData] = useState(initialFormData);


   const handleChange = (e) => {
                updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value.trim(),
                });
        };





useEffect(()=>{


client.onopen=()=>{

  console.log("web socket client connected");};


},[]);




  const handleSubmit = (e) => {
     client.send(JSON.stringify({
      type: "message",
      message: formData.comment,
    }));
    formData.comment = ''
    e.preventDefault();
  }















return(

<div className={classes.chatWindow}>

<ChatUserInfoTopBar/>

<ChatScreen/>



<div className={classes.chatInput}>





  <form className={classes.addCommentForm} onSubmit={handleSubmit}>

                   <div className={classes.inputTextBox} >
                          <textarea
                             type="text"
                             onChange={handleChange}
                             name="comment"
                             className={classes.inputText_field}
                             placeholder="Write your comment"
                             defaultValue=""
                          />
                    </div>









                 <button type="submit"  className= {classes.submit_button} ><b>Post comment </b> </button>
           </form>












</div>



</div>

);

}

export default ChatWindow;
