import axiosInstance from '../axios';
import {Logout} from './Logout';


export const getuser =({setData})=>{



    axiosInstance.get()
                  .then((res) => {
                        setData(data=>res.data);
                   })
                  .catch((error)=>{
                      if(error.response.status===401){
			 Logout();     
                      }

                   });

}



