import React,{useState} from 'react';
import classes from './TopToolBoxV1.module.css';
import {BsLayoutTextSidebarReverse} from 'react-icons/bs';



const TopToolBoxV1=(props)=>{


const TheIcon = props.icon;
//let Icon={props.toolBoxStyle.icon}


const [highLightStyle, setHighLightStyle]=useState({

	backgroundColor:"white",
	iconColor:"grey",
	iconTitleColor:"black"


}); 


 const sectionHandler=()=>{
    props.onPress();

    setHighLightStyle({
        backgroundColor:"var(--themeColor)",
        iconColor:"var(--themeColor)",
        iconTitleColor:"var(--themeColor)"	
    });
 }




return (

<div className={classes.parentDivBtn}>
  <button className={classes.topToolBoxV1} onClick={sectionHandler}>
      <TheIcon className={classes.toolBoxIcon} style={{color:highLightStyle.iconColor}}/>
      <div  style={{borderStyle: "none", 
		      margin:"auto", 
		      display:"flex",
		      justifyContent:"center",
		      fontSize:"20px",
		      color:highLightStyle.iconTitleColor}}>
	<b>{props.toolBoxStyle.iconName}</b>
      </div>	
  </button>

  <div className={classes.highLightDiv} style={{background: highLightStyle.backgroundColor}}>

  </div>	


</div>

);

}

export default TopToolBoxV1;
