import React,{useState,useEffect} from 'react'
import classes from './CourseViewDashboard.module.css';
import {BsFillCheckSquareFill} from 'react-icons/bs';
import { useHistory } from "react-router-dom";
import {BsFillTrashFill, BsPeopleFill} from 'react-icons/bs';
import {BiEdit} from 'react-icons/bi';
import {putcourseenroll} from '../../../../../CommonApps/AllAPICalls';



const CourseViewDashboard = (props)=>{

    let history = useHistory();
    const [style,setStyle]=useState({
          primary:'lightgrey',
          secondary:'lightgrey',
	  boxBkgColor: 'white', 
	  rightButtonColor: 'var(--themeColor)', 
	  codeColor:'grey', 
	  subjectColor: 'grey', 
	  boxShadow: 'rgba(0, 0, 0, 0.24) 0px 3px 8px',
	  fieldColor: 'grey', 

    });
    let deepColor='#ff5349'; //#ff5349
    let lightColor='#ffd4d1';  //#ffd4d1//#c2d8ff	

    const [enrollStatus, showEnrollStatus]= useState(false);
    const [enrollRequests,getEnrollRequests]= useState([]);
    const [data,setData]=useState({});
    const [selectedCourse, setSelectedCourse] = useState(localStorage.getItem('preferredCourseId'));	



    console.log('oooo: ', selectedCourse);
    const moveToSubject=()=>{
       history.push('/dashboard/subject');
    }

    const enrollInfoButtonHandler =()=>{
        showEnrollStatus(true);
    }


    useEffect(()=>{
            props.Course.enrolementrequests.map((requester,index)=>{

                       if(!props.Course.enrolledstudents.includes(requester)){
                             getEnrollRequests(enrollRequests => [...enrollRequests, requester]);
	               }   

             }
	    )

    },[props.Course.enrolementrequests,props.Course.enrolledstudents]);


    const [enrolledstudents,setEnrolledstudents] = useState(props.Course.enrolledstudents);

    const ApproveHandler = (userId)=>{
        let enrollId = userId;
        let courseId = props.Course.id;	
        enrolledstudents.push(enrollId);
        putcourseenroll({courseId, enrolledstudents});
        showEnrollStatus(false);
    }





    const courseSwitchHandler = ()=>{

      console.log("course Id: ", props.Course.id);
      localStorage.setItem('preferredCourseId', props.Course.id);
      window.location.reload(false);
    }


    //selectedCourse !== null && Number(selectedCourse) === props.Course.id &&


    useEffect(()=>{
      if(selectedCourse !== null && Number(selectedCourse) === props.Course.id){
            setStyle(style=>({
		    primary: deepColor, 
		    secondary: lightColor, 
		    boxBkgColor: lightColor,
		    rightButtonColor: deepColor,
		    codeColor: deepColor,
		    subjectColor: deepColor,
		    boxShadow: `${lightColor} 0px 3px 8px`,
		    fieldColor: '#c29d9b',

	    }));
      };

    },[props.Course.id, selectedCourse]);


   const [boxShadow, setBoxShadow]= useState('rgba(0, 0, 0, 0.24) 0px 0px 0px');

const MouseEnterHandler =()=>{
setBoxShadow(style.boxShadow);

}

const MouseLeaveHandler =()=>{
setBoxShadow('rgba(0, 0, 0, 0.24) 0px 0px 0px');

}



return (




<div className={classes.courseViewDashboard} 
	style={{borderColor: style.secondary,boxShadow: boxShadow}} 
	onMouseEnter={MouseEnterHandler} 
        onMouseLeave={MouseLeaveHandler}

	>



   <div className={classes.infoUnitBarParent}  onClick={moveToSubject} style={{backgroundColor: style.boxBkgColor}}>
       <div className={classes.infoUnitBar} style={{borderColor:style.secondary,backgroundColor: style.boxBkgColor}}>

        <div className={classes.subjectSection}> 
	   <span className={classes.signupIcon}> <BsFillCheckSquareFill/> </span>
	   <span className={classes.subjectTitle} style={{color: style.subjectColor}}> SUBJECT: </span>
	   <span className={classes.subjectName} style={{color: style.rightButtonColor}}>{props.Course.subject}</span>   

	</div>          


	<i className={classes.codeSection} style={{color: style.codeColor}}>
	  CODE: <span className={classes.courseCode}>
	         {props.Course.courseLocalCode} ({props.Course.courseGlobalCode})
	        </span>
	</i>




      </div>
   </div>




   <div className={classes.genInfoBar} style={{color: style.fieldColor}}>
      <div className={classes.designedFor}>
        DESIGNED FOR: <i style={{color:style.codeColor}}> B.Sc.{props.Course.designedFor}</i> 
      </div>

      <div className={classes.toolIcons}>
	{/*<span>  <BsPeopleFill className={classes.participantIcon}/> <span className={classes.numParticipant}>37 </span> </span>*/}
         <button className={classes.editButton} style={{color: style.rightButtonColor}}> <BiEdit/> </button>
	 {/*<button className={classes.deleteButton}> <BsFillTrashFill/> </button>*/}
       </div>

   </div>



   <div className={classes.genInfoBar} style={{color: style.fieldColor}}>
      <div className={classes.instructor}>
        INSTRUCTOR: <i style={{color:style.codeColor}}>Prof. Seema Bahinipati</i>
      </div>

      <div className={classes.toolIcons}>
         <button className={classes.editButton} onClick={enrollInfoButtonHandler} style={{color: style.rightButtonColor}}>

	       { enrollRequests.length>0 &&
	       <span className={classes.enrolledRequests}><i> {enrollRequests.length} </i></span>
               }

	       <BsPeopleFill /> 
	       <span className={classes.enrolledStuds}><i>{props.Course.enrolledstudents.length}</i></span> 
	 </button>

	{/*enrollment status div starts*/}
	{ enrollStatus && <div className={classes.enrollmentInfo}>


              { enrollRequests.length>0 &&

	            <>

		      {enrollRequests.map((userId,index)=>{
                           return <div className={classes.singleEnrollRequest} key={index}>
                                <i> New enrollment request from</i>
                                <i> Mr. Depak Samal</i>
                                <div className={classes.ApproveRejectBtn}>
                                  <button onClick={()=>ApproveHandler(userId)}> Approve </button>
                                  <button> Reject </button>
                                </div>
                             </div>
		             })
		      }	      
                    </>










              }
         </div>
	}
	{/*enrollment status div ends*/}
       </div>

   </div>

   <div className={classes.genInfoBar} style={{color: style.fieldColor}}>
      <div className={classes.instructor}>
         UPCOMING CLASS: <i style={{color:style.codeColor}}> 9:45 am |  27/02/2022</i>
      </div>


   </div>	

   <div className={classes.genInfoBar} style={{color: style.fieldColor}}>
       <div className={classes.status}>
        STATUS: <i> Ongoing</i>
       </div>


   </div>


   <div className={classes.genInfoBar} style={{color: style.fieldColor}}>
     {  selectedCourse === null && <button className={classes.switchTo} onClick={courseSwitchHandler}>
        Select 
      </button>
     }

     { selectedCourse !== null && Number(selectedCourse) !== props.Course.id &&
        <button className={classes.switchTo} onClick={courseSwitchHandler}>
           Select
        </button>
     }


     { selectedCourse !== null && Number(selectedCourse) === props.Course.id &&
        <button className={classes.switchTo} onClick={courseSwitchHandler} style={{color: '#ff5349',backgroundColor:'#ffd4d1'}}>
           Selected
        </button>
     }





      <div className={classes.viewDetail}>
        <i>View Detail</i>
      </div>




      <div className={classes.toolIcons}>
         <button className={classes.editButton} style={{color: style.rightButtonColor}}> <BsFillTrashFill/> </button>
       </div>

   </div>








</div>


);

}

export default CourseViewDashboard;
