         let meetingid = props.Class.id;	 
         console.log("user: ", props.userData.id);
	 console.log("host: ", props.Class.creater);     

       if( Number(props.userData.id) === Number(props.Class.creater) ){       	     
          deletemeeting({meetingid});	       
       window.location.reload(false);	
