   console.log(" Main App Page reredering-----------------");
   const [wcConnected, setWCConnected]= useState(false);
   let wcInstance;

   // wcInstance.onmessage = (event)=>{
   //  const dataFromServer = JSON.parse(event.data);
   //  console.log("Message receved-3", event.data);
   //}





