import React,{useRef,useEffect} from "react";
import classes from "./ChatScreen.module.css";


const ChatScreen=(props)=>{


const messages=props.messages;

const messagesEndRef = useRef(null);

const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" })
  }



useEffect(() => {
    scrollToBottom();
  }, [messages]);




return(

<div className={classes.chatScreen}>


     <div className={classes.dummyDiv1}>  Bibhuprasad   </div>


      {props.commentObj.map((comment,index)=>{

               return <div  key={index} className={classes.comment} >  

		      {comment.commenter === props.currentUser &&
		      <i className={classes.commentText}>{comment.commenttext}</i> 
                      }

                      {comment.commenter !== props.currentUser &&

                       <i className={classes.commentText} style={{backgroundColor: 'red', float: 'left'}}>{comment.commenttext}</i>

                      }

		      </div>




           }

        )}






         { messages.map((message,index)=>{

               return <div key={index} className={classes.comment}>   

                          <i className={classes.commentText}>  {message} </i>


		      </div>

           }

        ) 
      } 



     <div className={classes.dummyDiv} ref={messagesEndRef}>   </div>	









</div>	
);


}


export default ChatScreen;
