import classes from './CourseSyllabus.module.css';


const SectionLoop = (props) =>{



return <li key={sindex} className={classes.sectionHead} style={props.sectionEditStyle}>

                                              { (showSectionEdit.chapterId !==chapter.id || showSectionEdit.sectionId !==section.id) && <span>

                                                {
                                                    section.topics.map((topic, tindex)=>{

                                                        return <span key={tindex}>   {topic.name+", "}  </span>                                                                                                                       
                                                    })

                                                }

                                                  <EditSectionButton editSectionHandler={(event)=>editSectionHandler({chapterId, sectionId})}/>
                                                  <DeleteSectionButton deleteSectionHandler={deleteSectionHandler}/>

                                              </span>}

                                              { showSectionEdit.chapterId===chapter.id && showSectionEdit.sectionId===section.id &&

                                                  <textarea
                                                     type="text"
                                                     onChange={handleChange}
                                                     name="comment"
                                                     className={classes.sectionTextArea}
                                                     placeholder="Write your comment"
                                                     defaultValue={sectionText[csname]}
                                                  />

                                               }

                                             {  showSectionEdit.chapterId===chapter.id && showSectionEdit.sectionId===section.id &&
                                             <button
                                                  className={classes.sectionSaveButton}
                                                  onClick={(event)=>SaveSectionHandler({chapterId, sectionId})}
                                              >
                                                    Save
                                             </button>
                                             }

                                           </li>


}

export default SectionLoop;
