import React,{useState} from 'react';
import classes from './TopToolBar.module.css';
import TopToolBox from './TopToolBox';



const TopToolBar =()=>{




const [toolBoxStyle, setToolBoxStyle] = useState(
{
	buttonBkgColor:'#ff5349',
        boxBkgColor:'#ffd4d1',
	rightTopBoxColor: 'green'

});







return (


<div className={classes.topToolBar}>

   <div className={classes.tool} style={{backgroundColor:toolBoxStyle.boxBkgColor}}>  

      <div className={classes.upperHalf}>  
	 <button className={classes.actionButton} style={{backgroundColor:toolBoxStyle.boxBkgColor}}> Create a course </button>
	 <div className={classes.numOngoing} style={{borderColor:toolBoxStyle.boxBkgColor}}> 
	      <div className={classes.numOngoing_text}>Ongoing</div> 
	      <div className={classes.numOngoing_number}>3</div>
	 </div>
      </div> 

      <div className={classes.lowerHalf}>  
	<i><span>TOTAL COURSES :</span> <span className={classes.numCourses}> 5 </span></i>
      </div>
   </div>


 <TopToolBox/>
 <TopToolBox/>

 <TopToolBox/>	


</div>


);


}

export default TopToolBar;
