import React,{useState,Component} from 'react';
import classes from './TopInfoBar.module.css';
import TopInfoBarInstructor from './TopInfoBarInstructor';
import TopInfoBarButton from './TopInfoBarButton.js';



class TopInfoBar extends Component  {


constructor(props) {
    super(props);
   this.state = {
      bgColorProfile: "#E8E8E8",
      bgColorSettings: "#E8E8E8",
      bgColorCourses: "#E8E8E8",	   
      ColorProfile:"",
      ColorSettings: "",
      ColorCourses: "",	   

            }
   this.profileButton='Profile';
   this.settingsButton='Settings';
   this.coursesButton='Courses';	

  }



profileButtonHandler = (e) =>{

console.log("profile handler");

this.setState({
      bgColorProfile: "#919191",
      ColorProfile: "white",	
      bgColorSettings: "#E8E8E8",
      ColorSettings: "#686868",	
      bgColorCourses: "#E8E8E8",
      ColorCourses: "#686868",	
    })




}

settingsButtonHandler = (e) =>{

this.setState({
      bgColorProfile: "#E8E8E8",
      ColorProfile: "#686868",

      bgColorSettings: "#919191",
      ColorSettings: "white",

      bgColorCourses: "#E8E8E8",
      ColorCourses: "#686868",	
    })

}

coursesButtonHandler = (e) =>{
this.setState({
      bgColorProfile: "#E8E8E8",
      ColorProfile: "#686868",

      bgColorSettings: "#E8E8E8",
      ColorSettings: "#686868",


      bgColorCourses: "#919191",
      ColorCourses: "white",


    })




}






render(){
return (



<div className={classes.topInfoBar}>

       <TopInfoBarInstructor/>

       <TopInfoBarButton  onPress={this.profileButtonHandler}  bgColor={this.state.bgColorProfile} color={this.state.ColorProfile} buttonName={this.profileButton}/>

       <TopInfoBarButton onPress={this.settingsButtonHandler}  bgColor={this.state.bgColorSettings} color={this.state.ColorSettings} buttonName={this.settingsButton}/>

       <TopInfoBarButton onPress={this.coursesButtonHandler}  bgColor={this.state.bgColorCourses} color={this.state.ColorCourses} buttonName={this.coursesButton}/>




</div>



);

}



}


export default TopInfoBar;
