import React,{useState,useEffect} from 'react';
import {Route,Switch} from 'react-router-dom';
//import logo from './logo.svg';

import  './App.css';
import Header from './components/Header/Header';

import SideNavBar from './components/SideNavBar/SideNavBar';
import SideToolBar from './components/SideNavBar/SideToolBar';

import UserProfile from './MainApps/Account/UserProfile/UserProfile';
import Settings from './MainApps/Account/Settings/Settings';
import Courses from './MainApps/Account/Courses/Courses';


//Import Applications
import General from './MainApps/Dashboard/General/General';
import Subject from './MainApps/Dashboard/Subject/Subject';
import News from './MainApps/Dashboard/News/News';




import VATChat from './MainApps/Messages/VATChat/VATChat';
import EMail from './MainApps/Messages/EMail/EMail';
import Tickets from './MainApps/Messages/Tickets/Tickets';


import Overview from './MainApps/Class/Overview/Overview';
import Detail from './MainApps/Class/Detail/Detail';
import Specifics from './MainApps/Class/Specifics/Specifics';


import Login from './CommonApps/Login';
import axiosInstance from './axios';

import {getcoursesbyCourseId, getuser, getcoursesbyglobalCodeArray, getcoursesbyCourseIdArray} from './CommonApps/AllAPICalls';



function App() {




   const [sideNavBarWidth, setWidth] = useState('var(--sideNavBarWidth)');
   const [contract,setContract] = useState(true);

   const expandHandler=()=>{

        contract && setWidth('var(--sideNavBarWidthOnContract)');

       !contract && setWidth('var(--sideNavBarWidth)');

       setContract(!contract);
   }






   const [dashboardMounted, setDashboardMounted] = useState(false);
   const [messagesMounted, setMessagesMounted] = useState(false);
   const [classMounted, setClassMounted] = useState(false);
   const [tasksMounted, setTasksMounted] = useState(false);	
   const [booksMounted, setBooksMounted] = useState(false);
   const [examMounted, setExamMounted] = useState(false);	
   const [classmatesMounted, setClassmatesMounted] = useState(false);
   const [teacherMounted, setTeacherMounted] = useState(false);
   const [prepareMounted, setProgressMounted] = useState(false);
   const [analyticsMounted, setAnalticsMounted] = useState(false);	
   const [achievementsMounted, setAchievementsMounted] = useState(false);                
   const [goalsMounted, setGoalsMounted] = useState(false);
   const [calenderMounted, setCalenderMounted] = useState(false);
   const [diaryMounted, setDiaryMounted] = useState(false);
   const [helpcenterMounted, setHelpCenterMounted] = useState(false);

   const [selectedCourse, getCourseData] = useState([]);

   //const [courseData, getCourseData] = useState([]);


   let selectedCourseId = localStorage.getItem('preferredCourseId')

   useEffect(()=>{
     let courseId=selectedCourseId;

     getcoursesbyCourseId({courseId,getCourseData});
     return ()=>{
      getCourseData(selectedCourse=>[]);
     }
  },[selectedCourseId]);




   //  useEffect(() =>{

   //      axiosInstance.get().then((res) => {
   //                        setLoggedIn(loggedIn=>true);
                       // console.log(res.data);
   //                });

   //  },[]);	


   const [data,setData]=useState({});

   useEffect(()=>{
        getuser({setData});
    },[])



  const [dashboardCourses, getDashboardCourses] = useState([]);


  console.log("data: ", data);

    /* 
     useEffect(()=>{
       let courseIds = data.dashboardcourses;
       courseIds.map((courseId, index)=>{
           if(courseId !==0){
               getcoursesbyCourseIdArray({courseId, getDashboardCourses});
           }
       });

       return ()=>{
           getDashboardCourses(dashboardCourses=>[]);
       }
    },[data.dashboardcourses ]);
    */

















  return (
    <div className="edrapp">




    <Header onPress={expandHandler}  selectedCourse={selectedCourse} userData={data}/> 



    <SideToolBar toolBarWidth={sideNavBarWidth}/>


    <SideNavBar sideNavBarWidth={sideNavBarWidth} 
	dashboardMounted={dashboardMounted}  
	messagesMounted={messagesMounted} 

        classMounted = {classMounted} 
        tasksMounted = {tasksMounted} 
        booksMounted = {booksMounted} 
        examMounted = {examMounted} 
        classmatesMounted = {classmatesMounted} 
        teacherMounted = {teacherMounted} 
        prepareMounted = {prepareMounted} 
        analyticsMounted ={analyticsMounted} 
        achievementsMounted = {achievementsMounted} 
        goalsMounted = {goalsMounted} 
        calenderMounted = {calenderMounted} 
        diaryMounted = {diaryMounted} 
        helpcenterMounted = {helpcenterMounted} 
      />




    <Switch>




      <Route exact path='/account/userprofile' >
      <UserProfile sideNavBarWidth={sideNavBarWidth} selectedCourse={selectedCourse}/>
      </Route>

      <Route exact path='/account/settings' >
        <Settings sideNavBarWidth={sideNavBarWidth} selectedCourse={selectedCourse}/>
      </Route>

      <Route exact path='/account/courses' >
        <Courses sideNavBarWidth={sideNavBarWidth} selectedCourse={selectedCourse}/>
      </Route>


      <Route exact path='/dashboard/general' >
        <General sideNavBarWidth={sideNavBarWidth} passMountInfo={setDashboardMounted} selectedCourse={selectedCourse} />
      </Route>

     <Route exact path='/dashboard/subject' >
        <Subject sideNavBarWidth={sideNavBarWidth} passMountInfo={setDashboardMounted}  selectedCourse={selectedCourse}/>
      </Route>

      <Route exact path='/dashboard/news' >
        <News sideNavBarWidth={sideNavBarWidth} passMountInfo={setDashboardMounted}  selectedCourse={selectedCourse}/>
      </Route>






      <Route exact path='/messages/chat' >
        <VATChat sideNavBarWidth={sideNavBarWidth} passMountInfo={setMessagesMounted}  selectedCourse={selectedCourse}/>
      </Route>


      <Route exact path='/messages/email' >
        <EMail sideNavBarWidth={sideNavBarWidth} passMountInfo={setMessagesMounted}  selectedCourse={selectedCourse}/>
      </Route>



      <Route exact path='/messages/tickets' >
        <Tickets sideNavBarWidth={sideNavBarWidth} passMountInfo={setMessagesMounted}  selectedCourse={selectedCourse}/>
      </Route>


      <Route exact path='/class/overview' >
        <Overview sideNavBarWidth={sideNavBarWidth} passMountInfo={setClassMounted}  selectedCourse={selectedCourse}/>
      </Route>

      <Route exact path='/class/detail' >
        <Detail sideNavBarWidth={sideNavBarWidth} passMountInfo={setClassMounted}  selectedCourse={selectedCourse}/>
      </Route>

      <Route exact path='/class/specifics' >
        <Specifics sideNavBarWidth={sideNavBarWidth} passMountInfo={setClassMounted}  selectedCourse={selectedCourse}/>
      </Route>



    </Switch>	  



    </div>
  );




}

export default App;
