import React,{useState,useEffect,useRef} from "react";
import classes from "./ChatWindow.module.css"
import ChatUserInfoTopBar from "./ChatUserInfoTopBar";
import ChatScreen from "./ChatScreen";
import ChatInput from "./ChatInput";
import {w3cwebsocket as W3CWebSocket } from 'websocket';

import {client, SocketComponent} from "./WebSocket";



const ChatWindow = () =>{








  let roomname='class'


  const mountedRef = useRef(true);
  const textInput = useRef();

  const clearInput = () => (textInput.current.value = "");	


  const initialFormData = Object.freeze(

     {
         comment: "",
     }
  );



  const [formData, updateFormData] = useState(initialFormData)

  const handleChange = (e) => {
                updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value.trim(),
                });
        };





 const [state, setState] = useState(

  {
    messages: ['hello'],
    value: '',
  }
);


   console.log('rerendering');
   //const client = new W3CWebSocket('ws://127.0.0.1:8000/ws/chat/class/');




  const handleSubmit = (e) => {


    if (typeof client !== 'undefined') {	  

    if (client.readyState === WebSocket.CLOSED){
       console.log("websocket CLOSED"); 
    }




      client.send(JSON.stringify({
      type: "message",
      message: formData.comment,
    }))
    clearInput();	  
    state.value = ''

    }

    e.preventDefault();
  }






useEffect(()=>{


    client.onopen=()=>{
    console.log("web socket client connected");
   };


    client.onmessage = (message) => {
    const dataFromServer = JSON.parse(message.data);
    console.log('got reply! ', dataFromServer.message);

    if (dataFromServer !== ""  && mountedRef.current){

                     setState((state)=>({     
                              messages: [...state.messages,dataFromServer.message],
                              value: 'jai ganesh',
                                        }));

                              }}

    return () => { 
     mountedRef.current = false;
     client.close();	     
    }


},[]);

















return(

<div className={classes.chatWindow}>

<ChatUserInfoTopBar/>

<ChatScreen messages={state.messages}/>

<div className={classes.chatInput}>


{/*e => {  setState({ value: e.target.value });
                              }*/}

     <form className={classes.addCommentForm} onSubmit={handleSubmit}>
                   {/*
                   <div className={classes.inputTextBox} >
                          <textarea
                             type="text"
                             name="comment"
	                     value={state.value}
                             className={classes.inputText_field}
                             placeholder="Write your comment"
	                     onChange={e => {  setState({ value: e.target.value });
                              }}

                          />
                    </div>
                    */}


                  <div className={classes.chatInput} >
                          <input
                             ref={textInput}
                             type="text"
                             onChange={handleChange}
                             name="comment"
                             className={classes.inputTextBox}
                             placeholder="Write your comment"
                             defaultValue=""
                          />
                  </div>


                 <button type="submit"  className= {classes.submit_button} ><b>Post comment </b> </button>

	</form>




</div>



</div>

);

}

export default ChatWindow;
