import React,{useState,useEffect} from 'react'
import classes from './CourseViewDashboard.module.css';
import {BsFillCheckSquareFill} from 'react-icons/bs';
import { useHistory } from "react-router-dom";
import {BsFillTrashFill, BsPeopleFill} from 'react-icons/bs';
import {BiEdit} from 'react-icons/bi';
import {putcourseenroll} from '../../../../../CommonApps/AllAPICalls';



const CourseViewDashboard = (props)=>{

 let history = useHistory();

 const [style,setStyle]=useState(props.Course);
 const [enrollStatus, showEnrollStatus]= useState(false);
 const [enrollRequests,getEnrollRequests]= useState([]);

 const [data,setData]=useState({});

 const moveToSubject=()=>{
  history.push('/dashboard/subject');

 }



const enrollInfoButtonHandler =()=>{

showEnrollStatus(true);

}


useEffect(()=>{


     props.Course.enrolementrequests.map((requester,index)=>{

            if(!props.Course.enrolledstudents.includes(requester)){
                 getEnrollRequests(enrollRequests => [...enrollRequests, requester]);
	    }   

           })

},[props.Course.enrolementrequests,props.Course.enrolledstudents]);


const [enrolledstudents,setEnrolledstudents] = useState(props.Course.enrolledstudents);

const ApproveHandler = ()=>{

let userId = 8;
enrolledstudents.push(userId);

showEnrollStatus(false);
}




console.log("requests: ", enrollRequests);


return (

<div className={classes.courseViewDashboard}>



   <div className={classes.infoUnitBarParent}  style={{backgroundColor: style.infoBarBkgColor}} onClick={moveToSubject}>
       <div className={classes.infoUnitBar} style={{backgroundColor: style.infoBarBkgColor}}>

        <div className={classes.subjectSection}> 
	   <span className={classes.signupIcon}> <BsFillCheckSquareFill/> </span>
	   <span className={classes.subjectTitle}> SUBJECT: </span>
	   <span className={classes.subjectName}>{props.Course.subject}</span>   

	</div>          


	<i className={classes.codeSection}>
	  CODE: <span className={classes.courseCode}>
	         {props.Course.courseLocalCode} ({props.Course.courseGlobalCode})
	        </span>
	</i>




      </div>
   </div>




   <div className={classes.genInfoBar}>
      <div className={classes.designedFor}>
        DESIGNED FOR: <i>{props.Course.designedFor}</i> 
      </div>

      <div className={classes.toolIcons}>
	{/*<span>  <BsPeopleFill className={classes.participantIcon}/> <span className={classes.numParticipant}>37 </span> </span>*/}
         <button className={classes.editButton}> <BiEdit/> </button>
	 {/*<button className={classes.deleteButton}> <BsFillTrashFill/> </button>*/}
       </div>

   </div>



   <div className={classes.genInfoBar}>
      <div className={classes.instructor}>
        INSTRUCTOR: <i>Prof. Seema Bahinipati</i>
      </div>

      <div className={classes.toolIcons}>
         <button className={classes.editButton} onClick={enrollInfoButtonHandler}>

	       { enrollRequests.length>0 &&
	       <span className={classes.enrolledRequests}><i> {enrollRequests.length} </i></span>
               }

	       <BsPeopleFill /> 
	       <span className={classes.enrolledStuds}><i>{props.Course.enrolledstudents.length}</i></span> 
	 </button>

	{/*enrollment status div starts*/}
	{ enrollStatus && <div className={classes.enrollmentInfo}>


              { enrollRequests.length>0 &&

	            <>

		      {enrollRequests.map((userId,index)=>{
                           return <div className={classes.singleEnrollRequest} key={index}>
                                <i> New enrollment request from</i>
                                <i> Mr. Depak Samal</i>
                                <div className={classes.ApproveRejectBtn}>
                                  <button onClick={ApproveHandler}> Approve </button>
                                  <button> Reject </button>
                                </div>
                             </div>
		             })
		      }	      
                    </>










              }
         </div>
	}
	{/*enrollment status div ends*/}
       </div>

   </div>

   <div className={classes.genInfoBar}>
      <div className={classes.instructor}>
         UPCOMING CLASS: <i> 9:45 am |  27/02/2022</i>
      </div>


   </div>	

   <div className={classes.genInfoBar}>
       <div className={classes.status}>
        STATUS: <i> Ongoing</i>
       </div>


   </div>


   <div className={classes.genInfoBar}>
      <button className={classes.switchTo}>
        Switch to this course 
      </button>

      <div className={classes.viewDetail}>
        <i>View Detail</i>
      </div>




      <div className={classes.toolIcons}>
         <button className={classes.editButton}> <BsFillTrashFill/> </button>
       </div>

   </div>








</div>


);

}

export default CourseViewDashboard;
