import React,{useState,useEffect} from "react";
import classes from './UserProfile.module.css';
import TopInfoBar from './TopInfoBar';
import TopTitleBar from './TopTitleBar';
import ContentDiv from './ContentDiv';



const UserProfile = (props) =>{

  let title="User Profile";	


  const [toolBarWidth,setSideToolBarWith]=useState(props.sideNavBarWidth);

  const [profileWidth,setProfileWith]=useState('calc( 100% - var(--sideNavBarWidth) )')	


  const [contract, setContract] = useState(false);

  useEffect(() => {
      console.log('UserProfile page:  ',props.sideNavBarWidth);
      setSideToolBarWith(props.sideNavBarWidth);
     {!contract && setProfileWith('calc( 100% - var(--sideNavBarWidth) )')}
     {contract && setProfileWith('calc( 100% - var(--sideNavBarWidthOnContract) )')}
     setContract(!contract);

   }, [props.sideNavBarWidth]);

  const styles = {color: 'white',backgroundColor: '#919191'}




return (

<div className={classes.userProfileMainContainer} style={{left:toolBarWidth, width:profileWidth}}>


<TopInfoBar styles={styles}/>
<TopTitleBar title={title}/>		
<ContentDiv/>

</div>

);

}



export default UserProfile;
