import React,{useState,useEffect} from 'react';
import classes from './GeneralContentDiv.module.css';
import base from '../../CommonAppUtilities/AppContentDiv.module.css';

import TopToolBarTeacher from './components/Teacher/TopToolBar';
import AppointmentsAndDutiesTeacher from './components/Teacher/AppointmentsAndDuties';
import CoursesGridContainerTeacher from './components/Teacher/CoursesGridContainer'


import TopToolBarStudent from './components/Student/TopToolBar';
import AppointmentsAndDutiesStudent from './components/Student/AppointmentsAndDuties';
import CoursesGridContainerStudent from './components/Student/CoursesGridContainer'


import {getuser, getcoursesbyCourseId, getcoursesbyglobalCodeArray} from './../../../CommonApps/AllAPICalls';
import NoticeBoxContainer from './NoticeBoard/NoticeBoxContainer';





const GeneralContentDiv=(props)=>{

    const [data, setData] = useState({
       "dashboardcourses": []
    });

    const [courseData,getCourseData] = useState([]);
    const [showCourse,setShowCourse]=useState(true);
    const [rerender,setRerender] = useState(false);
    const [rerenderS,setRerenderS] = useState(false);
    const [showNoticeBoard, setShowNoticeBoard]= useState(false);


    const reRenderHandler=()=>{
      setRerender(!rerender);
      props.onPress();	    
    }

    const reRenderHandlerS=()=>{
      setRerenderS(!rerenderS);
    }


    useEffect(()=>{
        getuser({setData});
    },[rerenderS,rerender])


   /*	
    useEffect(()=>{
       let courseIds = data.dashboardcourses;
       courseIds.map((id, index)=>{
           if(id !==0){
               let globalCode=100000+id;
               getcoursesbyglobalCodeArray({globalCode, getCourseData});
           }
       });

       return ()=>{
           getCourseData(courseData=>[]);
       }
    },[data.dashboardcourses ]);
   */










    const noticeboardHandler=()=>{
       setShowCourse(false);
    }

    const courseHandler=()=>{
       setShowCourse(true);
    }

    const showTNoticeBoard=()=>{
        setShowNoticeBoard(true);
    }

    const closeTNoticeBoard=()=>{
       setShowNoticeBoard(false);
    }





return (

<div className={base.appContentDiv}>
   <div className={classes.contentDiv}>



	{/*Code for Teacher*/}
     {

        data.usertype===1 && <>	      
            <TopToolBarTeacher onPress={reRenderHandler}  showNoticeBoard={showTNoticeBoard}/>
		     {/*<AppointmentsAndDutiesTeacher/>*/}

                 { showNoticeBoard &&  <NoticeBoxContainer  closeNoticeBoard={closeTNoticeBoard} userData={data} courseData={props.dashboardCourses}/>}

		 { !showNoticeBoard && <CoursesGridContainerTeacher rerender={reRenderHandler} courseData={props.dashboardCourses} userData={data}/> }

	    </>

     }



     {

        data.usertype===2 && <>

            <TopToolBarStudent onPress={reRenderHandlerS} showNoticeBoard={showTNoticeBoard}/>

            <AppointmentsAndDutiesStudent/>


            { showNoticeBoard &&  <NoticeBoxContainer  closeNoticeBoard={closeTNoticeBoard} userData={data} courseData={props.dashboardCourses}/>}



             { !showNoticeBoard && <CoursesGridContainerStudent rerender={reRenderHandler} courseData={props.dashboardCourses} data={data}/> }


        </>

     }













   </div>
</div>	

);

}


export default GeneralContentDiv;
