import React,{useState,useEffect,useRef} from 'react';
import classes from './CourseSyllabus.module.css';
import {BsChevronDoubleDown, BsChevronDoubleUp,BsCheck,BsCheck2Circle} from "react-icons/bs";
import syllabusContent1 from './syllabusContent1.json';
import {BiEdit} from 'react-icons/bi';
import {getsyllabusfromId, getchapterfromId, getsectionfromId, gettopicfromId} from '../../../../CommonApps/AllAPICalls.js';
import ContentEditable from 'react-contenteditable';


const CourseSyllabus=(props)=>{



    const [syllabusData, getSyllabusData] = useState({"chapters":[]}); 

    const innerDivRef = useRef();
    const innerDiv = {html: "<b>Hello <i>World</i></b>"};	

    useEffect(()=>{

       let syllabusId = props.selectedCourse[0].syllabus;	   
       getsyllabusfromId({syllabusId,getSyllabusData});

       return ()=>{
           getSyllabusData(syllabusData=>({"chapters":[]}));
       }

    },[ props.selectedCourse[0].syllabus]);


   console.log("syllabus Data: ", syllabusData);


    const [showSyllabus, changeShowSyllabus]=useState(true);

    const showSyllabusHandler = ()=>{
      changeShowSyllabus(true);
    }


    const hideSyllabusHandler = ()=>{
       changeShowSyllabus(false);
    }


    const [showSectionEdit, setShowSectionEdit] = useState({chapterId:0,sectionId:0});

    const [sectionEditStyle, setSectionEditStyle] = useState({

	    borderStyle: 'none',
            borderWidth: '1px',
            borderColor: 'lightgrey',
            margin: '10px'

    });


    const editSectionHandler = ({chapterId, sectionId})=>{


       console.log("edit chapterId, sectionid : ",chapterId,'--',sectionId);

       setShowSectionEdit({chapterId: chapterId,sectionId: sectionId}); 


       /*setSectionEditStyle({
            borderStyle: 'solid',
            borderWidth: '1px',
            borderColor: 'lightgrey',
            margin: '10px',
	    minHeight:'100px'   


       });*/


    }



   const handleChange=(event)=>{

   console.log("Message: ", event.target.value);	   





   }

  const [sectionText, setSectionText] = useState({});

  //let sectionText={};

  useEffect(()=>{

  let sectionData="";

   syllabusData.chapters.map((chapter,cindex)=>{

       chapter.sections.map((section,sindex)=>{

	        sectionData="";
                section.topics.map((topic,tindex)=>{

	            sectionData=sectionData+topic.name;		
                    //console.log("chapter,section, topic:  ",chapter.id,"--",section.id,"--",topic.id);

	        });
	        let chapterId = chapter.id;
	        //console.log("chapterId: ",chapterId);
	        let sectionId = section.id;
	        let secName = 'c'+chapterId+'s'+sectionId;
	        //console.log("secName: ", secName);
                sectionText[secName]=sectionData;

       });

   });



  },[syllabusData]);


  //console.log("sectionText:   ",sectionText);





console.log("showSectionEdit.chapterId:   ",showSectionEdit.chapterId,'---', showSectionEdit.sectionId);




return (

<div className={classes.courseSyllabus}>

   <i> COURSE SYLLABUS:  
	{ !showSyllabus && <button onClick={showSyllabusHandler}> Show <BsChevronDoubleDown className={classes.ExpandSyllIcon}/>  </button> }

	{ showSyllabus && <button onClick={hideSyllabusHandler}> Hide <BsChevronDoubleUp className={classes.ExpandSyllIcon}/>  </button>}
   </i>

	{showSyllabus && syllabusData.length !==null &&<div className={classes.syllabusContent}>





       <ol className={classes.chapterNames}>



         {  

            syllabusData.chapters.map((chapter,index)=>{

	     return <li key={index} > <b>{chapter.name}</b>
			    <ol>

                              {chapter.sections.map((section, sindex)=>{

                                     let csname='c'+chapter.id+'s'+section.id;
				     let chapterId = chapter.id;
				     let sectionId =section.id; 

                                    return <li key={sindex} className={classes.sectionHead} style={sectionEditStyle}> 

					      { (showSectionEdit.chapterId !==chapter.id || showSectionEdit.sectionId !==section.id) && <span>


					        { 

                                                    section.topics.map((topic, tindex)=>{

							return <span key={tindex}>   {topic.name+", "}  </span>                                                                                                                  

					            })


					        }
                                              </span>}

					      {/*showSectionEdit && 
					           <div contentEditable={true}
						      suppressContentEditableWarning={true}
						      className={classes.sectionEditBox}
						      onChange={handleChange}
						    > 

						      {section.topics.map((topic, tindex)=>{

							      return <span key={tindex} >   {topic.name+", "}  </span>

                                                          })
				                      }				      

                                               </div>

                                              */}


				              { showSectionEdit.chapterId===chapter.id && showSectionEdit.sectionId===section.id && 


                                                  <textarea
                                                     type="text"
                                                     onChange={handleChange}
                                                     name="comment"
                                                     className={classes.sectionTextArea}
                                                     placeholder="Write your comment"
                                                     defaultValue={sectionText[csname]}
                                                  />


					       }	      



					     <button 
				                  className={classes.sectionEditButton} 
				                  onClick={(event)=>editSectionHandler({chapterId, sectionId})}
					      >
					            <BiEdit/> 
					     </button> 

					   </li> 



			         }
			      )}

                         {/*  			                          
                            chapter.sectons.map((section,sindex)=>{
				    return <li key={sindex} className={classes.topicName}>{section.name} 

                                         { 
                                         <span className={classes.numLectures}  style={{backgroundColor:'green'}}> 2 lectures 

					 </span>
                                         }


   				      </li>

                             })
                         */}
		       </ol>

		   </li>	    
	    })


	   }



       </ol>




   </div>}

</div>

);

}

export default CourseSyllabus;
