import ChatWindow from './ChatWindow/ChatWindow';
import {getallusers,getuser} from '../../../CommonApps/AllAPICalls';




const VATChatContentDiv=()=>{


const [usersData, setUsersData ] = useState([
   {
        "id": 1,
        "firstname": "Mr Dummy ",
        "lastname": "User",
        "email": "dummy.user@gmail.com",
        "username": "dummy1988",
        "usertype": "student",
        "profile_image": "http://127.0.0.1:8000/media/codingwithmitch/logo_1080_1080.png",
        "registrationid": "WEDR1000001",
        "gender": "male",
        "position": "MTech",
        "dateofbirth": "2022-01-03"
    }


]);


const [data, setData]=useState(  {
        "id": 1,
        "firstname": "Mr Dummy ",
        "lastname": "User",
        "email": "dummy.user@gmail.com",
        "username": "dummy1988",
        "usertype": "student",
        "profile_image": "http://127.0.0.1:8000/media/codingwithmitch/logo_1080_1080.png",
        "registrationid": "WEDR1000001",
        "gender": "male",
        "position": "MTech",
        "dateofbirth": "2022-01-03"
    }
);


useEffect(() =>{

   getallusers({ setUsersData});
   getuser({setData});


  },[]);



const [clickedUserId,setClickedUserId]=useState(1);

const switchUserHandler=(userId)=>{

setClickedUserId(userId);
console.log("swicth user",userId);


}


useEffect(()=>{



},[clickedUserId]);






return (

<div className={base.appContentDiv}>

 <div className={classes.contentDiv}>



   <ChatNavBar usersData={usersData} data={data} onPress={switchUserHandler}/>	


   <ChatWindow clickedUserId={clickedUserId}/>






 </div>
</div>

);

}


export default VATChatContentDiv;
