import React,{useState,useEffect} from 'react';
import base from '../../CommonAppUtilities/AppContentDiv.module.css';
import classes from './VATChatContentDiv.module.css';
import ChatNavBar from './ChatNavBar/ChatNavBar';
import ChatWindow from './ChatWindow/ChatWindowTest';
import {getallusers,getuser} from '../../../CommonApps/AllAPICalls';
//import {w3cwebsocket as W3CWebSocket } from 'websocket';



const VATChatContentDiv=()=>{


 const [usersData, setUsersData ] = useState([
   {
        "id": 1,
        "firstname": "Mr Dummy ",
        "lastname": "User",
        "email": "dummy.user@gmail.com",
        "username": "dummy1988",
        "usertype": "student",
        "profile_image": "http://127.0.0.1:8000/media/codingwithmitch/logo_1080_1080.png",
        "registrationid": "WEDR1000001",
        "gender": "male",
        "position": "MTech",
        "dateofbirth": "2022-01-03"
    }

 ]);


 const [userData, setData]=useState(  {
        "id": 1,
        "firstname": "Mr Dummy ",
        "lastname": "User",
        "email": "dummy.user@gmail.com",
        "username": "dummy1988",
        "usertype": "student",
        "profile_image": "http://127.0.0.1:8000/media/codingwithmitch/logo_1080_1080.png",
        "registrationid": "WEDR1000001",
        "gender": "male",
        "position": "MTech",
        "dateofbirth": "2022-01-03"
    }
 );


useEffect(() =>{

   getallusers({ setUsersData});
   getuser({setData});


  },[]);



const [clickedUserId,setClickedUserId]=useState(1);
const [clickedGroupId,setClickedGroupId]=useState(0);


const switchUserHandler=(userId,groupId)=>{

setClickedUserId(userId);
setClickedGroupId(groupId);

}






return (

<div className={base.appContentDiv}>

 <div className={classes.contentDiv}>


   <ChatNavBar usersData={usersData} data={userData} onPress={switchUserHandler} clickedGroupId={clickedGroupId}/>	


   <ChatWindow clickedUserId={clickedUserId}  clickedGroupId={clickedGroupId}   data={userData} />



 </div>
</div>

);

}


export default VATChatContentDiv;
