import classes from './TicketsContentDiv.module.css';
import TicketNavBar from './TicketNavBar/TicketNavBar';
import TicketViewWindow from './TicketViewWindow/TicketViewWindow';
import axiosInstance from '../../../axios';


const TicketsContentDiv=()=>{


const [ticketId, setTicketId]=useState(1);

const [data, setData ] = useState(
    {
        "id": 6,
        "title": "GGGGG",
        "author": 1,
        "category": 1,
        "excerpt": "cdcd",
        "content": "adadfvdfv dc sd asdkad clkASD, ASDAS",
        "status": "open",
        "pstatus": "draft",
        "priority": "high",
        "resolution": "unresolved",
        "created_at": "2021-12-22T11:48:10.381307Z"
    });



	{/*

 async function fetchData(){

      const request = await axiosInstance.get(`tickets/${ticketId}/`);
      setData(data=>request.data);
      console.log("data inside ticketContentDiv:  ",request.data);          
      return request;

    }

    fetchData();


			*/}











useEffect(() =>

{
      console.log("Querying for ticketID data inside TicketContentDiv...");	
      axiosInstance.get(`tickets/${ticketId}/`).then((res) => {
                        setData(data=>res.data);
                        console.log('data inside TicketContentDiv',res.data);
                })
               .catch((error)=>{
                     if(error.response.status===401){
                      window.location.href = '/login/';

                      }


                })







  },[ticketId]);


const ticketClickHandler=(ticketId)=>{

console.log("ticket click hander: ",ticketId);
setTicketId(ticketId);
}








return (

<div className={base.appContentDiv}>	

  <div className={classes.contentDiv}>



     <TicketNavBar onPress={ticketClickHandler}/>


     <TicketViewWindow ticketId={ticketId} data={data}/>


  </div>

</div>

);

}


export default TicketsContentDiv;
