import React,{useState,useEffect} from "react";
import classes from "./LeftUserBoxContainer.module.css"
import LeftUserBox from "./LeftUserBox";
import classimg from '../../../../images/groupImage.png';
import user1Image from '../../../../images/girl1.jpeg'
import {getallusers,getuser} from '../../../../CommonApps/AllAPICalls';

import basewebURL from "../../../../basewebURL";


const LeftUserBoxContainer = (props) =>{



let groupname="Classical Mechanics-I";
let user1="Reena Samant";

const [usersData, setUsersData ] = useState(props.usersData);
const [data, setData]=useState(props.data );


console.log(props.usersData);


useEffect(()=>{

setUsersData(usersData=>props.usersData);

},[props.usersData]);











return(

<div className={classes.leftUserBoxContainer}>

<LeftUserBox userImage={classimg} userName={groupname} />


      {/* in future change this logic. This must interfere performance*/}

      {usersData.filter(
           function(user) {
         if (user.id === data.id) {
              return false; 
              }
                  return true;
             })


            .map((user,index)=>{



             return    <LeftUserBox key={index} userImage={user.profile_image} userName={user.firstname +" "+user.lastname}  />



           }

        )}







</div>

);

}

export default LeftUserBoxContainer;
