import React,{useState} from 'react';
import classes from "./CourseSecToggleBar.module.css";
import CourseSyllabus from './CourseSyllabus';


const CourseSecToggleBar = ()=>{



const [privateInfo, setPrivateInfo] = useState(true);

const [ubuttonStyle, setUButtonStyle] = useState({

	borderColor:'lightblue'

});

const [sbuttonStyle, setSButtonStyle] = useState({

        borderColor:'lightgrey'

});




const privateInfoHandler = ()=>{
setPrivateInfo(true);
setUButtonStyle({borderColor:'lightblue'});
setSButtonStyle({borderColor:'lightgrey'});	
}


const publicInfoHandler = () =>{

setPrivateInfo(false);
setUButtonStyle({borderColor:'lightgrey'});
setSButtonStyle({borderColor:'lightblue'});


}




return (


<div className={classes.courseSecToggleBar}>
 <div className={classes.courseSecToggleBarButtons}>
    <button className={classes.privateInfo} onClick={privateInfoHandler} style={ubuttonStyle}> <b>COURSE UPDATE</b> </button>


    <button className={classes.publicInfo} onClick={publicInfoHandler} style={sbuttonStyle}> COURSE SYLLABUS </button>	
 </div>


  {!privateInfo && <CourseSyllabus/>}

  {privateInfo && <div className={classes.updateDiv}>

  course update

  </div>}









</div>

);


}


export default CourseSecToggleBar;
