import React,{useState,useEffect} from 'react';
import logo from './logo.svg';
import  './App.css';
import Header from './components/Header/Header';
import {Route,Switch, BrowserRouter} from 'react-router-dom';
import CourseInfo from './components/Courses/CourseInfo';
import SideNavBar from './components/SideNavBar/SideNavBar';
import SideToolBar from './components/SideNavBar/SideToolBar';
import UserProfile from './allAppsStudent/Account/UserProfile';
import Settings from './allAppsStudent/Account/Settings';




function App() {


const [sideBarWidth, setWidth] = useState('15rem');
const [contract,setContract] = useState(true);


const expandHandler=()=>{
console.log("expand n contract");

{contract && setWidth('3rem');}

{!contract && setWidth('15rem');}

setContract(!contract);
}







return (
    <div className="edrapp">

    <Header onPress={expandHandler} /> 

    <SideToolBar toolBarWidth={sideBarWidth}/>

    <SideNavBar wInfo={sideBarWidth} />



    <Switch>
      <Route path='/courseinfo' exact="true">
      <CourseInfo/>
      </Route>

      <Route path='/account/userprofile' exact="true">
      <UserProfile/>
      </Route>

      <Route path='/account/settings' exact="true">
      <Settings/>
      </Route>



    </Switch>	  




    </div>
  );
}

export default App;
