import React,{useState,useEffect,useRef} from "react";
import classes from "./ChatWindow.module.css"
import ChatUserInfoTopBar from "./ChatUserInfoTopBar";
import ChatScreen from "./ChatScreen";
import ChatInput from "./ChatInput";
import {w3cwebsocket as W3CWebSocket } from 'websocket';
import { Picker } from "emoji-mart";
import "emoji-mart/css/emoji-mart.css";
import Login from '../../../../CommonApps/Login';
import axiosInstance from '../../../../axios';
import {MdSend} from 'react-icons/md';
import {postchatcomment,getchatcomments} from '../../../../CommonApps/AllAPICalls';





function connect (){

let client;
let messageListeners = [];
let isConnected= false;
let reconnectOnClose = true;

function on(fn) {
    messageListeners.push(fn);
  }

 function off(fn) {
    messageListeners = messageListeners.filter(l => l !== fn);
  }


   const close = client.close;

   client.close = () => {
      reconnectOnClose = false;
      close.call(client);
    }




function start () {

client = new W3CWebSocket('ws://127.0.0.1:8000/ws/chat/class/');

client.onclose = () => {
isConnected=false;
console.log('time 1');
//setTimeout(start, 5000);
console.log('time 2');
}

client.onopen=()=>{
isConnected=true;
}

client.onmessage = (event)=>{
const dataFromServer = JSON.parse(event.data);	
messageListeners.forEach(fn => fn(dataFromServer.message));
}


}

start();

return {
    on,
    off,	
    getClient: () => client,
    isConnected: () => isConnected,
    close: () => client.close(),	
   }
}

const client = connect();



function useMessages() {
  const [messages, setMessages] = useState(['hi']);

  useEffect(() => {
    function handleMessage(message) {
      setMessages([...messages, message]);
    }
      client.on(handleMessage);
     return () => client.off(handleMessage);
  }, [messages, setMessages]);

  return messages;
}







const ChatWindow = (props) =>{


   const mountedRef = useRef(true);

   const [isConnected, setIsConnected] = useState(client.isConnected());

   console.log('isConnected: ', isConnected);	

   const [commentObj,setChatCommentObj]=useState([{}]);

   const [message, setMessage] = useState('');


   const messages = useMessages();  


   const handleChange = (e) => {
      setMessage(msg=>e.target.value);


   };

   useEffect(()=>{

   return ()=>{
    mountedRef.current = false;

   }


   },[]);





   function sendMessage(e) {
    e.preventDefault();
    if(message !== ""){	   
    client.getClient().send(JSON.stringify({
      type: "message",
      message: message,
    }))
    }

    console.log('sending message', message);	 
    setMessage('');
   }









return(

<div className={classes.chatWindow}>

<ChatUserInfoTopBar clickedUserId={props.clickedUserId} />

<ChatScreen messages={messages}  commentObj={commentObj}/>

<div className={classes.chatInput}>




     <form className={classes.addCommentForm} onSubmit={sendMessage}>


                  <div className={classes.chatInput} >

                      <input className={classes.inputTextBox} value={message} onChange={handleChange} />


                  </div>


                 <button type="submit"  className= {classes.submit_button} ><b><MdSend className={classes.sendButtonIcon}/> </b> </button>

     </form>


</div>




</div>

);

}

export default ChatWindow;
