import React,{useState} from "react";
import classes from "./AddComment.module.css";
import {BsPlus} from "react-icons/bs";

const AddComment = () =>{


  const [textBox, showTextBox ] = useState(false);

  const initialFormData = Object.freeze({

        comment: "NoComments",

        });




  const [formData, updateFormData] = useState(initialFormData)

  const handleChange = (e) => {
                updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value.trim(),
                });
        };









  const textBoxHandler=()=>{

   showTextBox(true);
 }


  const handleSubmit = (e) => {
                e.preventDefault();	  
   }



return(

<div className={classes.ticketComments}>


  <div className={classes.commentTitle}> 
	<button className={classes.commenterButton}  onClick={textBoxHandler} >  <BsPlus/><span>Add a comment</span> </button> 
  </div>


	{ textBox && <div className={classes.descriptionTextBox}>  


           <form className={classes.addCommentForm} onSubmit={handleSubmit}>	

                   <div className={classes.textarea_Div} >
                          <textarea
                             type="text"
                             onChange={handleChange}
                             name="comment"
                             className={classes.inputText_field}
                             placeholder="Write your comment"
                             defaultValue=""
                          />
                    </div>









                <button type="submit"  className= {classes.submit_button} ><b>Create </b> </button>
           </form>

        </div> }


 <div> 
  <hr className={classes.hrLine}/>

</div>	




</div>

);

}

export default AddComment;
