import React,{useState,useEffect} from "react";
import classes from "./LeftUserBox.module.css"
import MateIconBox from "./MateIconBox";
import MateInfoBox from "./MateInfoBox";
import {getuserbyId} from '../../../../CommonApps/AllAPICalls';
import basewebURL from "../../../../basewebURL";


const LeftUserBox = (props) =>{


const [userData, setUserData ] = useState({


    "id": 1,
    "firstname": "Mr Dummy",
    "lastname": "User",
    "email": "dummy.user@gmail.com",
    "username": "bibhu",
    "usertype": "teacher",
    "profile_image": "/media/codingwithmitch/logo_1080_1080.png",
    "registrationid": "EDR1000001",
    "gender": "Male",
    "position": "K-12",
    "dateofbirth": "2022-01-21"



});

const userId = props.userId;


    useEffect(()=>{
    getuserbyId({ userId,setUserData});

  },[userId]);




let imageurl=basewebURL+userData.profile_image;





return(

<div className={classes.leftUserBox}>
<button className={classes.leftUserBoxButton} onClick={props.onPress} style={props.style} >

<MateIconBox userImage={imageurl} />

<MateInfoBox userName={userData.firstname+" "+userData.lastname} />


</button>
</div>

);

}

export default LeftUserBox;
