import React, { Component } from 'react';
import axiosInstance from '../../../../axios';
class ProfileImageTest extends Component {

  state = {
    email: 'bibhu.phy@gmailcom',
    username: 'bibhu',
    profile_image: '',
  };

  handleChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value
    })
  };

  handleImageChange = (e) => {
    this.setState({
      image: e.target.files[0]
    })
  };

  handleSubmit = (e) => {
    e.preventDefault();
    console.log(this.state);
    let form_data = new FormData();
    form_data.append('profile_image', this.state.profile_image, this.state.profile_image.name);
    form_data.append('email', this.state.email);
    form_data.append('username', this.state.username);
    let url = 'http://localhost:8000/api/posts/';
    axiosInstance.put(`/`, form_data, {
      headers: {
        'content-type': 'multipart/form-data'
      }
    })
        .then(res => {
          console.log(res.data);
        })
        .catch(err => console.log(err))
  };

  render() {
    return (
      <div className="App">
        <form onSubmit={this.handleSubmit}>
          <p>
            <input type="text" placeholder='email' id='title' value={this.state.email} onChange={this.handleChange} required/>
          </p>
          <p>
            <input type="text" placeholder='username' id='content' value={this.state.username} onChange={this.handleChange} required/>

          </p>
          <p>
            <input type="file"
                   id="image"
                   accept="image/png, image/jpeg"  onChange={this.handleImageChange} required/>
          </p>
          <input type="submit"/>
        </form>
      </div>
    );
  }
}

export default ProfileImageTest;
