import classes from "./TicketComments.module.css";
import style from "./AddComment.module.css";
import {BsPlus} from "react-icons/bs";

const AddComment = () =>{


const [textBox, showTextBox ] = useState(false);



  const textBoxHandler=()=>{

   showTextBox(true);
 }


  const handleSubmit = (e) => {
                e.preventDefault();	  
   }



return(

<div className={classes.ticketComments}>


  <div className={classes.commentTitle}> 
	<button className={style.commenterButton}  onClick={textBoxHandler} >  <BsPlus/><span>Add a comment</span> </button> 
  </div>


	{ textBox && <div className={style.descriptionTextBox}>  


           <form className={classes.addCommentForm} onSubmit={handleSubmit}>	

                   <div className={classes.name_inputDiv} >
                          <textarea
                             type="text"
                             onChange={handleChange}
                             name="description"
                             className={classes.inputText_field}
                             placeholder="Write your comment"
                             defaultValue=""
                          />
                    </div>









                <button type="submit"  className= {classes.submit_button} ><b>Create </b> </button>
           </form>

        </div> }


 <div> 
  <hr className={classes.hrLine}/>

</div>	




</div>

);

}

export default AddComment;
