import React,{useState} from "react";
import classes from "./LeftUserBox.module.css"
import MateIconBox from "./MateIconBox";
import MateInfoBoxNewUser from "./MateInfoBoxNewUser";




const LeftUserBox = (props) =>{

const [displayAdd, setDisplayAdd]= useState('300px');


const addUserHandler =()=>{

setDisplayAdd('0');
}




return(

<div className={classes.leftUserBox} style={{width:displayAdd}}>
<button className={classes.leftUserBoxButton} onClick={addUserHandler} >

<MateIconBox userImage={props.userImage} />

<MateInfoBoxNewUser userName={props.userName}  added={props.added}/>


</button>
</div>

);

}

export default LeftUserBox;
