import classes from './NoticeBoxContainer.module.css';
import NoticeBox from './NoticeBox';
import NoticeBoardToolBar from './NoticeBoardToolBar';
import {getuser, getnotice, getnoticebyId} from '../../../../CommonApps/AllAPICalls';



const NoticeBoxContainer =(props)=>{


const [read, setRead]=useState(false);
const [unread, setUnRead]=useState(true);

const [noticeIds, getNoticeIds]=useState([]);	

const [noticeData, getNoticeData]= useState([]);


console.log('dashboard courses notice array: ', props.courseData)



useState(()=>{


   //getnotice({getNoticeData});
        let noticeIdArray=[]
	{   props.courseData.map((course, index)=>{

                       course.noticearray.map((noticeId, indexI)=>{  
			       console.log('noticeId: ', noticeId)
                             if ( noticeIdArray.indexOf(noticeId) < 0){noticeIdArray.push(noticeId)}

		       })




	          }
	    );   
        }

    getNoticeIds(noticeIds=>noticeIdArray);	

 return ()=>{
  getNoticeIds(noticeIds=>[]);

 }


},[props.userData.id]);



console.log('noticeId array: ', noticeIds);




return (

<div className={classes.noticeBoxContainer}>


   <NoticeBoardToolBar closeNoticeBoard={props.closeNoticeBoard}/>



   {  noticeData.map((notice, index)=>{

          return <NoticeBox read={read} Notice={notice} key={index}/>

      })
   }




</div>

);

}


export default NoticeBoxContainer;
