        "status": "open",
        "pstatus": "draft",
        "priority": "high",
        "resolution": "unresolved",
        "created_at": "2021-12-22T11:48:10.381307Z"
      }]);


     const [allTicketData, getAllTicketData] = useState(null);


      useEffect(() =>{
        getalltickets({setData});
      },[]);	


      useEffect(()=>{
        let courseId = props.selectedCourse[0].id;
