import React,{useState, useRef,useEffect} from 'react';
import classes from './UserHead.module.css';
import { FaCaretDown } from "react-icons/fa";
import userImage from '../../img/BibhuprasadMahakud.jpg'
import UserHeadDropDown from './UserHeadDropDown';




const UserHead =(props)=>{

const [dropDown, setDropDown] = useState(false);


const ref= useRef();


useEffect(()=>{


   const checkIfClickedOutside = e => {

       if(dropDown && ref.current && !ref.current.contains(e.target)){setDropDown(false)}
   }


  document.addEventListener("mousedown", checkIfClickedOutside)


	return () => {
      // Cleanup the event listener
      document.removeEventListener("mousedown", checkIfClickedOutside)
    }


 },[dropDown]);








const clickHandler = () =>{

console.log('clicked!');
setDropDown(true);


}


const clickItem = ()=>{

setDropDown(false);

}



return (

<div className={classes.UserHeadDiv}>
<button className={classes.UserHeadButton} onClick={clickHandler}>
  <i className={classes.UserFirstName}> Bibhuprasad</i>	
  <FaCaretDown className={classes.UsersIconHead} />
  <img className={classes.userImage} src={userImage} alt='edr Logo' />

</button>

  { dropDown && <UserHeadDropDown  ref={ref} buttonClick={dropDown} clickItem={clickItem}  />}

</div>


);

}


export default UserHead;
