import React,{useState} from 'react';
import classes from './TopToolBarV1.module.css';
import {BsChevronDown} from 'react-icons/bs';



const TopToolBarLeft=()=>{



        const showUsertypeChangeHandler=()=>{

           setShowDropDown(showDropDown=>!showDropDown);

        }

       const [showDropDown, setShowDropDown] = useState(false);







return (

        <div className={classes.searchBoxParentDiv}>
                <div className={classes.searchBoxDiv}> <span>Dashboard</span> </div>
                <div className={classes.userTypeInfo}>
                      {props.userData.usertype===1 && <div>
                                      <span>You  are a</span>
                                      <span className={classes.buttonsDivTnS}>
                                          <button className={classes.teacherButton} onClick={showUsertypeChangeHandler}>
                                             <span>Teacher </span>
                                             <span className={classes.downArrowSpan}><BsChevronDown/></span>
                                          </button>

                                           {showDropDown && <button className={classes.studentButton}> Student </button>}

                                       </span>
                                    </div>
                      }
                      {props.userData.usertype===2 && <span>
                                      You  are a <button>
                                                     Student <BsChevronDown/>
                                                 </button>
                                      </span>
                      }
                </div>
         </div>





);

}

export default TopToolBarLeft;
