import React,{useState} from 'react';

import classes from './TopInfoBar.module.css';
import TopInfoBarInstructor from './TopInfoBarInstructor';
import TopInfoBarButton from './TopInfoBarButton.js';

const TopInfoBar=()=>{


let profileButton='Profile';
let settingsButton='Settings';
let coursesButton='Courses';

const [profileButtonActive, setProfileButtonActive] = useState(false);

const [settingsButtonActive, setSettingsButtonActive] = useState(false);

const [coursesButtonActive, setCoursesButtonActive] = useState(false);	


const profileButtonHandler = () =>{

setSettingsButtonActive(false);
setCoursesButtonActive(false);
setProfileButtonActive(true);	
}



const settingsButtonHandler = () =>{

setProfileButtonActive(false);
setCoursesButtonActive(false);
setSettingsButtonActive(true);
}


const coursesButtonHandler = () =>{

setProfileButtonActive(false);
setSettingsButtonActive(false);
setCoursesButtonActive(false);	
}











return (



<div className={classes.topInfoBar}>

       <TopInfoBarInstructor/>

       <TopInfoBarButton  onClick={profileButtonHandler} cstate={profileButtonActive} buttonName={profileButton}/>

       <TopInfoBarButton onClick={settingsButtonHandler} cstate={settingsButtonActive} buttonName={settingsButton}/>

       <TopInfoBarButton onClick={coursesButtonHandler} cstate={coursesButtonActive} buttonName={coursesButton}/>


</div>



);

}


export default TopInfoBar;
