import React, { useState,CSSProperties, useFocus } from 'react';
import axiosInstance from '../axios';
import { Link } from 'react-router-dom';
//import classes from "./Login.module.css"

import classes from './Register.module.css';

import LoginHeader from "./LoginHeader";
import LoginFooter from "./LoginFooter";
import logoImage from './BWlogo.JPG'
//import { useHistory } from "react-router-dom";

import {changepassword} from './AllAPICalls';
import FadeLoader from "react-spinners/FadeLoader";

import OTPinputUnit from './OTPinputUnit';



const override: CSSProperties = {
  display: "block",
  margin: "0 auto",
  borderColor: "red",
};









const Login =(props)=> {


   let color="var(--themeColor);";
   const initialFormData = Object.freeze({
	        username:'',
		email: '',
		password: '',
	});

   const [formData, updateFormData] = useState(initialFormData);


   const [fieldColor, setFieldColor] = useState({borderColor: "lightgrey",borderWidth:"1px"});



   const [buttonStyle, setButtonStyle]= useState({
      color:'grey',
      backgroundColor:'lightgrey'

    });



   const [loginState, setLoginState] = useState("OTPnotrequested");

   const [inputRef, setInputFocus] = useFocus();


   const handleChange = (e) => {
		updateFormData({
			...formData,
			[e.target.name]: e.target.value.trim(),
		});
	};



   const handleChangeUserName = (event) => {

        updateFormData({
                        ...formData,
                        [event.target.name]: event.target.value.trim(),
                });


        console.log("Value: ", event.target.value.length);

        if(event.target.value.length===10){
           setButtonStyle({
            color:'white',
            backgroundColor:'var(--themeColor)'
           });
        }else{
        setButtonStyle({
            color:'grey',
            backgroundColor:'lightgrey'
           });

        }

    }



















	const handleSubmit = (e) => {
		e.preventDefault();
		console.log(formData);

		axiosInstance.post(`token/`, {username: formData.username,password: "OLsbd!@#45"+formData.password,})
			.then((res) => {
				localStorage.setItem('access_token', res.data.access);
				localStorage.setItem('refresh_token', res.data.refresh);
				axiosInstance.defaults.headers['Authorization'] =
					'JWT ' + localStorage.getItem('access_token');
				props.setLoggedIn(true);
				//history.push('/');
				//console.log(res);
				//console.log(res.data);
			});
	};   


   const mouseOver=()=>{

   setFieldColor({borderColor: "var(--themeColor)",borderWidth:"1px"});

   }

   const mouseLeave=()=>{

   setFieldColor({borderColor: "lightgrey",borderWidth:"1px"});

   }



  const [showOTPspace, setShowOTPspace]= useState(false);



   const handleSubmitGetOTP=(e)=>{
    e.preventDefault();

    if(formData.username.length !==10){

     alert("Enter a 10 digit number");

    }


    formData["username"] = "+91"+formData.username;
    setLoginState(loginState=>"OTPsending");

    changepassword({formData, setLoginState});
    setShowOTPspace(true);


   } 


   const handleChangeOTP=(e)=>{

     updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value.trim(),
                });




   }






   console.log("formData: ", formData);



return (



<div className={classes.registerParent}>


   <LoginHeader/>


     { loginState==="OTPsending" &&
          <div className={classes.registerDiv}>
               <div className={classes.titleDiv}> <h2>Creating ... </h2> </div>
               <div style={{margin:'auto'}}>
                   <FadeLoader color={color} loading={true} css={override} size={50}   />
               </div>
          </div>
    }







   { loginState ==="OTPnotrequested" && <>


     <div className={classes.registerDiv}>


      <div className={classes.titleDiv}> <h2> Login </h2> </div> 

      <div className={classes.switchMethodDiv}>  </div>


      <div className={classes.phoneNumberDiv}>
         <div className={classes.phonetitle}>Enter mobile number </div>

         <div className={classes.enterPhoneDiv}>

          <div className={classes.countryCodes}>

            <img
               alt="India"
               className={classes.inflag}
               src="http://purecatamphetamine.github.io/country-flag-icons/3x2/IN.svg"/>
               <i> +91</i>

          </div>

           <input
             type="number"
	     max="9" 
	     Min="0"
             onChange={handleChangeUserName}
             name="username"
             placeholder="Enter mobile number"
             className={classes.usernameInput}
          />



       </div>

       <div className={classes.phonetnc}>By continuing, you agree to our  Terms of Service  and  Privacy Policy  </div>
     </div>


     <div className={classes.submitButtonDiv}>

           <button type="button" onClick={handleSubmitGetOTP} className={classes.sendOTPButton} style={buttonStyle}> <b>Send OTP</b>  </button>

     </div>





    </div>
    </>
    }



    { loginState==="OTPsent" && <>


       <div className={classes.registerDiv}>		    

       <div className={classes.titleDiv}> <h2> Login </h2> </div>

       <div className={classes.switchMethodDiv}>  </div>


       <div className={classes.phoneNumberDiv}>
           <div className={classes.phonetitle}>Enter OTP </div>

           <div className={classes.enterOTPDiv}>



	     <OTPinputUnit handleChangeOTP={handleChangeOTP} name="first"/>	     
             <OTPinputUnit handleChangeOTP={handleChangeOTP} name="second"/>
	     <OTPinputUnit handleChangeOTP={handleChangeOTP} name="third"/>	    
             <OTPinputUnit handleChangeOTP={handleChangeOTP} name="fourth"/>
             <OTPinputUnit handleChangeOTP={handleChangeOTP} name="fifth"/>













           </div>


         <div className={classes.phonetnc}>By continuing, you agree to our  Terms of Service  and  Privacy Policy  </div>


       </div>



        <div className={classes.submitButtonDiv}>

           <button type="button" onClick={handleSubmit} className={classes.sendOTPButton} style={buttonStyle}> <b>Submit OTP</b>  </button>

       </div>






       </div>


    </>

   }


</div>

);






}

export default Login;
