import React,{useState} from "react";
import classes from "./ProfileImage.module.css";
import basewebURL from "../../../../basewebURL";
import axiosInstance from '../../../../axios';

const ProfileImage = (props) => {


let imageurl=basewebURL+props.data.profile_image




const initialFormData = Object.freeze({
                profile_image:props.data.profile_image,
        });



const [formData, updateFormData] = useState(initialFormData);

const [visibility,setVisibility] = useState(true);

const handleChange = (e) => {
                updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value.trim(),
                });

       setVisibility(false);
        };



const handleSubmit = (e) => {
                e.preventDefault();
                console.log('form data profile image: ----',formData);

                axiosInstance
                        .put(`/`,{
        "email":props.data.email,
        "username":props.data.username,
        "firstname": formData.profile_image,


                        })
                        .then((res) => {
                                console.log('-----------------',res.data);
                        });
         window.location.reload(true);
        };









return (


<div className={classes.profileImageDiv}>


 <img className={classes.userImage} src={imageurl} alt='edr Logo' />


  <form className={classes.imageUpload} onSubmit={handleSubmit}>

        <input
              type="file"
              onChange={handleChange}
              name="profile_image"
	      accept="image/*"
              className={classes.image_field}
              placeholder="profile image"
          />




       <button type="submit"  className= {classes.submit_button} ><b>Upload </b> </button>	


  </form>

</div>
);


}


export default ProfileImage;
