import React,{useState,useEffect} from 'react';
import classes from './CourseSyllabus.module.css';
import {BsChevronDoubleDown, BsChevronDoubleUp,BsCheck,BsCheck2Circle} from "react-icons/bs";
import syllabusContent1 from './syllabusContent1.json';

import {getsyllabusfromId, getchapterfromId, getsectionfromId, gettopicfromId} from '../../../../CommonApps/AllAPICalls.js';



const CourseSyllabus=(props)=>{



    const [syllabusData, getSyllabusData] = useState({"chapters":[]}); 
    const [chapterData, getChapterData] = useState([]);    
    const [sectionData, getSectionData] = useState();
    const [allSyllabusData, setAllSyllabusData] = useState([]);



    useEffect(()=>{

       let syllabusId = props.selectedCourse[0].syllabus;	   
       getsyllabusfromId({syllabusId,getSyllabusData});

       return ()=>{
           getSyllabusData(syllabusData=>({"chapters":[]}));
       }

    },[ props.selectedCourse[0].syllabus]);


    //console.log("syllabusData---", syllabusData);


    useEffect(()=>{       
     let chapters=syllabusData.chapters;
        if(chapters.length>0){
           chapters.map((chapterId,index)=>{        
		   getchapterfromId({chapterId, getChapterData});
	   });
	}





      return ()=>{
        getChapterData(chapterData=>([]));
      }

    },[syllabusData.chapters]);



    //console.log("All Syllabus Data: ", allSyllabusData);


    useEffect(()=>{

      let allData = [];

      if(chapterData.length>0){

	      chapterData.map((chapterObj, chaIndex)=>{

                      chapterObj.sections.map((sectionId, secIndex)=>{

			      //console.log("chapter Id, secId: ",chapterObj.id, sectionId );
			      getsectionfromId({sectionId, getSectionData});                         
			      //console.log("section Data: ", sectionData);
		      });
	      });
         }

    },[chapterData]);









    //console.log("chapterData: ", chapterData);
    //console.log("section Data: ", sectionData);	




    const [showSyllabus, changeShowSyllabus]=useState(true);

    const showSyllabusHandler = ()=>{
      changeShowSyllabus(true);
    }


    const hideSyllabusHandler = ()=>{
       changeShowSyllabus(false);
    }





return (

<div className={classes.courseSyllabus}>

   <i> COURSE SYLLABUS:  
	{ !showSyllabus && <button onClick={showSyllabusHandler}> Show <BsChevronDoubleDown className={classes.ExpandSyllIcon}/>  </button> }

	{ showSyllabus && <button onClick={hideSyllabusHandler}> Hide <BsChevronDoubleUp className={classes.ExpandSyllIcon}/>  </button>}
   </i>

	{showSyllabus && <div className={classes.syllabusContent}>





       <ol className={classes.chapterNames}>



         {

            Object.keys(syllabusContent1).map((chapter,index)=>{

	     return <li key={index} > <b>{chapter}</b>
                      <ol>
                         {			

                            Object.keys(syllabusContent1[chapter]).map((topic,tindex)=>{

				    return <li key={tindex} className={classes.topicName}>{topic} 


                                        { syllabusContent1[chapter][topic][0] ==="complete" &&
                                         <span className={classes.numLectures}  style={{backgroundColor:'green'}}> 2 lectures 


					 </span>


                                         }


                                         { syllabusContent1[chapter][topic][0] ==="incomplete" &&
                                         <span className={classes.numLectures}  style={{backgroundColor:'red'}}> 2 lectures


                                         </span>


                                         }





				    </li>

                             })
                         }
                      </ol>

		   </li>	    
	    })


	 }



       </ol>




   </div>}

</div>

);

}

export default CourseSyllabus;
