import React,{useState} from 'react';
import logo from './logo.svg';
import  './App.css';
import Header from './components/Header/Header';
import {Route,Switch} from 'react-router-dom';
import CourseInfo from './components/Courses/CourseInfo';
import SideNavBar from './components/SideNavBar/SideNavBar';
import SideToolBar from './components/SideNavBar/SideToolBar';
import UserProfile from './allAppsStudent/Account/UserProfile/UserProfile';
import Settings from './allAppsStudent/Account/Settings/Settings';
import Courses from './allAppsStudent/Account/Courses/Courses';

//Import Applications
import Main from './allAppsStudent/Dashboard/Main/Main';
import View2 from './allAppsStudent/Dashboard/View2/View2';
import View3 from './allAppsStudent/Dashboard/View3/View3';


import VATChat from './allAppsStudent/Messages/VATChat/VATChat';
import EMail from './allAppsStudent/Messages/EMail/EMail';
import Tickets from './allAppsStudent/Messages/Tickets/Tickets';


import Current from './allAppsStudent/Class/Current/Current';
import Previous from './allAppsStudent/Class/Previous/Previous';
import All from './allAppsStudent/Class/All/All';








function App() {

   const [sideNavBarWidth, setWidth] = useState('var(--sideNavBarWidth)');
   const [contract,setContract] = useState(true);

   const expandHandler=()=>{

       {contract && setWidth('var(--sideNavBarWidthOnContract)');}

       {!contract && setWidth('var(--sideNavBarWidth)');}

       setContract(!contract);
   }


   const [dashboardMounted, setDashboardMounted] = useState(false);
   const [messagesMounted, setMessagesMounted] = useState(false);
   const [classMounted, setClassMounted] = useState(false);
   const [tasksMounted, setTasksMounted] = useState(false);	
   const [booksMounted, setBooksMounted] = useState(false);
   const [examMounted, setExamMounted] = useState(false);	
   const [classmatesMounted, setClassmatesMounted] = useState(false);
   const [teacherMounted, setTeacherMounted] = useState(false);
   const [prepareMounted, setProgressMounted] = useState(false);
   const [analyticsMounted, setAnalticsMounted] = useState(false);	
   const [achievementsMounted, setAchievementsMounted] = useState(false);                
   const [goalsMounted, setGoalsMounted] = useState(false);
   const [calenderMounted, setCalenderMounted] = useState(false);
   const [diaryMounted, setDiaryMounted] = useState(false);
   const [helpcenterMounted, setHelpCenterMounted] = useState(false);






return (
    <div className="edrapp">




    <Header onPress={expandHandler} /> 

    <SideToolBar toolBarWidth={sideNavBarWidth}/>


    <SideNavBar sideNavBarWidth={sideNavBarWidth} 
	dashboardMounted={dashboardMounted}  
	messagesMounted={messagesMounted} 

        classMounted = {classMounted} 
        tasksMounted = {tasksMounted} 
        booksMounted = {booksMounted} 
        examMounted = {examMounted} 
        classmatesMounted = {classmatesMounted} 
        teacherMounted = {teacherMounted} 
        prepareMounted = {prepareMounted} 
        analyticsMounted ={analyticsMounted} 
        achievementsMounted = {achievementsMounted} 
        goalsMounted = {goalsMounted} 
        calenderMounted = {calenderMounted} 
        diaryMounted = {diaryMounted} 
        helpcenterMounted = {helpcenterMounted} 
      />


    <VATChat sideNavBarWidth={sideNavBarWidth} passMountInfo={setMessagesMounted}  />



    <Switch>
      <Route path='/courseinfo' exact="true">
      <CourseInfo/>
      </Route>

      <Route path='/account/userprofile' exact="true">
      <UserProfile sideNavBarWidth={sideNavBarWidth}/>
      </Route>

      <Route path='/account/settings' exact="true">
        <Settings sideNavBarWidth={sideNavBarWidth}/>
      </Route>

      <Route path='/account/courses' exact="true">
        <Courses sideNavBarWidth={sideNavBarWidth}/>
      </Route>


      <Route path='/dashboard/main' exact="true">
        <Main sideNavBarWidth={sideNavBarWidth} passMountInfo={setDashboardMounted}  />
      </Route>

     <Route path='/dashboard/view2' exact="true">
        <View2 sideNavBarWidth={sideNavBarWidth} passMountInfo={setDashboardMounted}  />
      </Route>

      <Route path='/dashboard/view3' exact="true">
        <View3 sideNavBarWidth={sideNavBarWidth} passMountInfo={setDashboardMounted}  />
      </Route>






      <Route path='/messages/chat' exact="true">
        <VATChat sideNavBarWidth={sideNavBarWidth} passMountInfo={setMessagesMounted}  />
      </Route>

      <Route path='/messages/email' exact="true">
        <EMail sideNavBarWidth={sideNavBarWidth} passMountInfo={setMessagesMounted}  />
      </Route>


      <Route path='/messages/tickets' exact="true">
        <Tickets sideNavBarWidth={sideNavBarWidth} passMountInfo={setMessagesMounted}  />
      </Route>


      <Route path='/class/current' exact="true">
        <Current sideNavBarWidth={sideNavBarWidth} passMountInfo={setClassMounted}  />
      </Route>

      <Route path='/class/previous' exact="true">
        <Previous sideNavBarWidth={sideNavBarWidth} passMountInfo={setClassMounted}  />
      </Route>

      <Route path='/class/all' exact="true">
        <All sideNavBarWidth={sideNavBarWidth} passMountInfo={setClassMounted}  />
      </Route>

















    </Switch>	  

    </div>
  );
}

export default App;
