import React,{useState} from 'react';

import classes from './TopInfoBar.module.css';
import TopInfoBarInstructor from './TopInfoBarInstructor';
import TopInfoBarButton from './TopInfoBarButton.js';

const TopInfoBar=()=>{


let profileButton='Profile';
let settingsButton='Settings';
let coursesButton='Courses';

const [profileButtonActive, setProfileActiveButton] = useState(false);

const [settingsButtonActive, setSettingsActiveButton] = useState(false);

const [coursesButtonActive, setCoursesActiveButton] = useState(false);	


const profileButtonHandler = () =>{

settingsButtonActive(false);
coursesButtonActive(false);
profileButtonActive(true);	
}



const settingsButtonHandler = () =>{

profileButtonActive(false);
coursesButtonActive(false);
settingsButtonActive(true);
}


const coursesButtonHandler = () =>{

profileButtonActive(false);
settingsButtonActive(false);
coursesButtonActive(false);	
}











return (



<div className={classes.topInfoBar}>

       <TopInfoBarInstructor/>

       <TopInfoBarButton  onClick={profileButtonHandler} buttonName={profileButton}/>

       <TopInfoBarButton buttonName={settingsButton}/>

       <TopInfoBarButton buttonName={coursesButton}/>


</div>



);

}


export default TopInfoBar;
