import React,{useState, useEffect} from 'react';
import classes from './SideNavBar.module.css';
import SideNavBarButton from './SideNavBarButton';
import SideToolBar from './SideToolBar';
import { BsSpeedometer,BsChatLeftText,BsUiChecksGrid,BsPencilSquare,BsPeople,BsFilePerson,BsHammer,BsBook } from "react-icons/bs";
import {BsGraphUp,BsZoomIn,BsTrophy,BsHandThumbsUp,BsListOl,BsCalendarDay,BsJournalText,BsQuestionSquare} from 'react-icons/bs';
import {SiGoogleclassroom} from "react-icons/si";
import {GiTeacher} from "react-icons/gi";
import { useHistory } from "react-router-dom";







const SideNavBar=(props)=>{

   let history = useHistory();
   let activeButtonColor="linear-gradient(to right, var(--sideNavBarBtnhoverColor) 90%, var(--themeColor) 0%)";
   let inActivebuttonColor="var(--sideNavBarBkgColor)";




   const [sideNavBarWidth, setSideNavBarWidth] = useState(props.sideNavBarWidth);




   const [dashboardButtonColor, setDashoardButtonColor]=useState(inActivebuttonColor);
   const [messagesButtonColor, setMessagesButtonColor]=useState(inActivebuttonColor);	
   const [classButtonColor, setClassButtonColor]=useState(inActivebuttonColor);  
   const [tasksButtonColor, setTasksButtonColor]=useState(inActivebuttonColor);
   const [booksButtonColor, setBooksButtonColor]=useState(inActivebuttonColor);
   const [examButtonColor, setExamButtonColor]=useState(inActivebuttonColor);
   const [classmatesButtonColor, setClassmatesButtonColor]=useState(inActivebuttonColor);
   const [teacherButtonColor, setTeacherButtonColor]=useState(inActivebuttonColor);	
   const [prepareButtonColor, setPrepareButtonColor]=useState(inActivebuttonColor);
   const [progressButtonColor, setProgressButtonColor]=useState(inActivebuttonColor);
   const [analyticsButtonColor, setAnlyticsButtonColor]=useState(inActivebuttonColor);
   const [achievementsButtonColor, setachievementsButtonColor]=useState(inActivebuttonColor);
   const [goalsButtonColor, setGoalsButtonColor]=useState(inActivebuttonColor);
   const [calenderButtonColor, setCalenderButtonColor]=useState(inActivebuttonColor);
   const [diaryButtonColor, setDiaryButtonColor]=useState(inActivebuttonColor);
   const [helpcenterButtonColor, setHelpCenterButtonColor]=useState(inActivebuttonColor)



   useEffect(() => {
    // console.log('props value: ',props.sideNavBarWidth);
     setSideNavBarWidth(props.sideNavBarWidth);
   }, [props.sideNavBarWidth]);




   //for dashboard
   useEffect(()=>{

      console.log("dahboard mount info in sideNavBar:", props.dashboardMounted);

        {!props.dashboardMounted && setDashoardButtonColor(inActivebuttonColor)};
	{props.dashboardMounted && setDashoardButtonColor(activeButtonColor)};    


   },[props.dashboardMounted]);



   //for messages	
   useEffect(()=>{ 

      console.log("messages mount info inSideNavBar:", props.messagesMounted);

      {!props.messagesMounted && setMessagesButtonColor(inActivebuttonColor)};
      {props.messagesMounted && setMessagesButtonColor(activeButtonColor)}; 	   

   },[props.messagesMounted]);









   const dashboardHandler=()=>{
   history.push('/dashboard');
   }

   const messagesHandler=()=>{
   history.push('/messages/chat');
   }

   const classHandler=()=>{
   history.push('/class');
   setButtonColor({classColor:activeButtonColor});	   
   }

   const tasksHandler=()=>{
   history.push('/tasks');
   setButtonColor({tasksColor:activeButtonColor});	   
   }	

   const booksHandler=()=>{
   history.push('/books');
   setButtonColor({booksColor:activeButtonColor});	   
   }

   const examHandler=()=>{
   history.push('/exam');
   setButtonColor({examColor:activeButtonColor});	   
   }

   const classmatesHandler=()=>{
   history.push('/classmates');
   setButtonColor({classmatesColor:activeButtonColor});	   
   }

   const teacherHandler=()=>{
   history.push('/teacher');
   setButtonColor({teacherColor:activeButtonColor});	   
   }

   const prepareHandler=()=>{
   history.push('/prepare');
   setButtonColor({prepareColor:activeButtonColor});	   
   }

   const progressHandler=()=>{
   history.push('/progress');
   setButtonColor({progressColor:activeButtonColor});	   
   }

   const analyticsHandler=()=>{
   history.push('/analyics');
   setButtonColor({analyticsColor:activeButtonColor});	   
   }

   const achievementsHandler=()=>{
   history.push('/achievements');
   setButtonColor({achievementsColor:activeButtonColor});	   
   }

   const goalsHandler=()=>{
   history.push('/goals');
   setButtonColor({goalsColor:activeButtonColor});	   
   }

   const calenderHandler=()=>{
   history.push('/calender');
   setButtonColor({calenderColor:activeButtonColor});	   
   }


   const diaryHandler=()=>{
   history.push('/diary');
   setButtonColor({diaryColor:activeButtonColor});	  
   }

   const helpCenterHandler=()=>{
   history.push('/helpcenter');
   setButtonColor({helpcenterColor:activeButtonColor});	   
   }










   return (

     <div className={classes.SideMainContainer} style={{width:sideNavBarWidth}} >


        <SideNavBarButton onPress={dashboardHandler}   icon={BsSpeedometer} name="Dashboard" buttonColor={{background:dashboardButtonColor }}/>

        <SideNavBarButton onPress={messagesHandler}  icon={BsChatLeftText} name="Messages" buttonColor={{background:messagesButtonColor }}/>

        <SideNavBarButton onPress={classHandler} icon={GiTeacher} name="Class" buttonColor={{background:buttonColor.classColor }} />

        <SideNavBarButton onPress={tasksHandler} icon={BsUiChecksGrid} name="Tasks"buttonColor={{background:buttonColor.tasksColor }} />

        <SideNavBarButton onPress={booksHandler} icon={BsBook} name="Books"  buttonColor={{background:buttonColor.booksColor }} />

        <SideNavBarButton onPress={examHandler} icon={BsPencilSquare} name="Exam"  buttonColor={{background:buttonColor.examColor }} />

        <SideNavBarButton onPress={classmatesHandler} icon={BsPeople} name="Classmates"  buttonColor={{background:buttonColor.classmatesColor }} />	

        <SideNavBarButton onPress={teacherHandler} icon={BsFilePerson} name="Teacher"  buttonColor={{background:buttonColor.teacherColor }} />

        <SideNavBarButton onPress={prepareHandler} icon={BsHammer} name="Prepare"  buttonColor={{background:buttonColor.prepareColor }} />

        <SideNavBarButton onPress={progressHandler} icon={BsGraphUp} name="Progress"  buttonColor={{background:buttonColor.progressColor }} />

        <SideNavBarButton onPress={analyticsHandler} icon={BsZoomIn} name="Analytics"  buttonColor={{background:buttonColor.analyticsColor }} />	

        <SideNavBarButton onPress={achievementsHandler} icon={BsTrophy} name="Achievements"  buttonColor={{background:buttonColor.achievementsColor }} />

        <SideNavBarButton onPress={goalsHandler} icon={BsListOl} name="Goals"  buttonColor={{background:buttonColor.goalsColor }} />	

        <SideNavBarButton onPress={calenderHandler} icon={BsCalendarDay} name="Calender"  buttonColor={{background:buttonColor.calenderColor }} />

        <SideNavBarButton onPress={diaryHandler} icon={BsJournalText} name="Diary"  buttonColor={{background:buttonColor.diaryColor }} />

        <SideNavBarButton onPress={helpCenterHandler} icon={BsQuestionSquare} name="Help Center"  buttonColor={{background:buttonColor.helpcenterColor }} />


     </div>

   );




}




export default SideNavBar;
