import React,{useState} from 'react'; 
import classes from './HeaderLeft.module.css';
import companyLogo from './img/logoBlueWhite.PNG'
//import {Link} from 'react-router-dom';
import {FaBars} from 'react-icons/fa';
import CourseDropDown from './CourseDropDown';
import { useHistory } from "react-router-dom";

import {IoMdArrowRoundBack} from 'react-icons/io';

function HeaderLeft(props){

    let history = useHistory();
    const [courseDropDown, setCourseDropDown]=useState(false);


    const showCourses=()=>{
       setCourseDropDown(true);
    }


    const hideCourses=()=>{
      setCourseDropDown(false);
    }


   const dashClickHandler=()=>{

   history.push("../../dashboard/general");	   

   }


   const backToDashboard=()=>{
     localStorage.removeItem('preferredCourseId');
     history.push('/dashboard/general');



   }	







return (

 <div className={classes.headerLeft} > 



      <button type="button" className={classes.backButton}> <IoMdArrowRoundBack className={classes.BackIcon}/> </button>

      <img className={classes.logo} src={companyLogo} alt='edr Logo' />

      <button className={classes.ExpConButton} onClick={props.onPress}> <FaBars className={classes.ExpConIcon} /></button>	

      <div className={classes.courseNameButton}  onMouseOver={showCourses} onMouseLeave={hideCourses} > 
 	 <i> { props.selectedCourse.length>0 &&  props.selectedCourse[0].subject}   </i>
	 <i onClick={dashClickHandler} className={classes.clickDash}> { props.selectedCourse.length===0 &&  <span>Dashboard</span>} </i>
	 { props.selectedCourse.length>0 && <sup className={classes.superScript}>@ {props.selectedCourse[0].designedFor} </sup> }

         { courseDropDown && props.selectedCourse.length !==0 && <CourseDropDown userData={props.userData} dashboardCourses={props.dashboardCourses} />}

      </div>	




  </div>

);


}

export default HeaderLeft;
