import React,{useState, useEffect} from 'react';
import classes from './SideNavBar.module.css';
import SideNavBarButton from './SideNavBarButton';
import SideToolBar from './SideToolBar';
import { BsSpeedometer,BsChatLeftText,BsUiChecksGrid,BsPencilSquare,BsPeople,BsFilePerson,BsHammer,BsBook } from "react-icons/bs";
import {BsGraphUp,BsZoomIn,BsTrophy,BsHandThumbsUp,BsListOl,BsCalendarDay,BsJournalText,BsQuestionSquare} from 'react-icons/bs';
import {SiGoogleclassroom} from "react-icons/si";
import {GiTeacher} from "react-icons/gi";
import { useHistory } from "react-router-dom";







const SideNavBar=(props)=>{

   let history = useHistory();
   let activeButtonColor="linear-gradient(to right, var(--sideNavBarBtnhoverColor) 90%, var(--themeColor) 0%)";
   let inActivebuttonColor="var(--sideNavBarBkgColor)";



   const [sideNavBarWidth, setSideNavBarWidth] = useState(props.wInfo);
   const [buttonColor,setButtonColor] = useState(inActivebuttonColor);



   useEffect(() => {
     console.log('props value: ',props.wInfo);
     setSideNavBarWidth(props.wInfo);
   }, [props.wInfo]);


   const dashboardHandler=()=>{
   history.push('/dashboard');
   setButtonColor(activeButtonColor);	   
   }




   return (

     <div className={classes.SideMainContainer} style={{width:sideNavBarWidth}} >


        <SideNavBarButton onPress={dashboardHandler}   icon={BsSpeedometer} name="Dashboard" buttonColor={{background:buttonColor }}/>

        <SideNavBarButton onPress={messagesHandler}  icon={BsChatLeftText} name="Messages"/>

        <SideNavBarButton onPress={classHandler} icon={GiTeacher} name="Class"/>

        <SideNavBarButton onPress={TasksHandler} icon={BsUiChecksGrid} name="Tasks"/>

        <SideNavBarButton onPress={booksHandler} icon={BsBook} name="Books"/>

        <SideNavBarButton onPress={examHandler} icon={BsPencilSquare} name="Exam"/>

        <SideNavBarButton onPress={classmatesHandler} icon={BsPeople} name="Classmates"/>	

        <SideNavBarButton onPress={teacherHandler} icon={BsFilePerson} name="Teacher"/>

        <SideNavBarButton onPress={prepareHandler} icon={BsHammer} name="Prepare"/>

        <SideNavBarButton onPress={progressHandler} icon={BsGraphUp} name="Progress"/>

        <SideNavBarButton onPress={analyticsHandler} icon={BsZoomIn} name="Analytics"/>	

        <SideNavBarButton onPress={achievementsHandler} icon={BsTrophy} name="Achievements"/>

        <SideNavBarButton onPress={goalsHandler} icon={BsListOl} name="Goals"/>	

        <SideNavBarButton onPress={calenderHandler} icon={BsCalendarDay} name="Calender"/>

        <SideNavBarButton onPress={diaryHandler} icon={BsJournalText} name="Diary"/>

        <SideNavBarButton onPress={helpCenterHandler} icon={BsQuestionSquare} name="Help Center"/>


     </div>

   );




}




export default SideNavBar;
