return (

<div className={classes.aboutTheCourse}>

   <i className={classes.titleSpace}> <span>ABOUT THE COURSE </span>  <button> <BiEdit className={classes.editIcon}/></button>   </i>


   <div className={classes.aboutTheCourseText}> 


