import React,{useState,useEffect} from "react";
import classes from './TopInfoBarInstructor.module.css';
import {getuserbyId} from '../../CommonApps/AllAPICalls';


const TopInfoBarInstructor = ( props) => {

const [userData, setUserData]=useState({
	usertitle: "",
	firstname:"",
	lastname:""

});

console.log("sel Course", props.selectedCourse[0].teacher);

useEffect(()=>{

let userId=props.selectedCourse[0].teacher;

getuserbyId({userId, setUserData});

},[props.selectedCourse]);


console.log('userData', userData);


return (


<div className={classes.topInfoBar__instructor}> <i className={classes.topInfoBar__instructorName}>Instructor: {userData.usertitle} {userData.firstname} {userData.lastname}</i></div>


);	



}


export default TopInfoBarInstructor;
