import React,{useState, useEffect} from "react";
import classes from "./ChatUserInfoTopBar.module.css";
//import user1Image from '../../../../images/girl1.jpeg'
import TopBarChatUserTools from "./TopBarChatUserTools";
import {getuserbyId} from '../../../../CommonApps/AllAPICalls';
//import basewebURL from "../../../../basewebURL";

const ChatUserInfoTopBar=(props)=>{



const [userData, setUserData] = useState({
    "id": 1,
    "firstname": "Bibhuprasad",
    "lastname": "Mahak",
    "email": "bibhu.phy@gmail.com",
    "username": "bibhu",
    "usertype": "student",
    "profile_image": "/media/images/BibhuProfileImage.jpeg",
    "registrationid": "EDR1000001",
    "gender": "Male",
    "position": "BSc",
    "dateofbirth": "2022-01-20"
});

const userId=props.clickedUserId;

useEffect(()=>{

getuserbyId({userId, setUserData});

},[userId]);


let imageurl=userData.profile_image;

return(

<div className={classes.chatUserInfoTopBar}>

    <div className={classes.userNameImageDiv}>

       <img className={classes.userImage} src={imageurl} alt='edr Logo' />

       <div className={classes.userName}> {userData.firstname} {userData.lastname} </div>       

    </div>



  <TopBarChatUserTools/>




</div>	
);



}


export default ChatUserInfoTopBar;
